  /* Main checkbox container */
  .skill-checkbox {
    display: inline-flex;
    align-items: flex-start;
    gap: var(--skill-spacing-sm, 8px);
    cursor: pointer;
    user-select: none;
    position: relative;
    margin: 0;
    font-family: var(--skill-font-family, inherit);
