      display: flex;
      align-items: center;
      gap: var(--spacing-xs, 8px);
    }

    /* Expand icon */
    .skill-collapse__expand-icon {
      width: 20px;
      height: 20px;
      color: var(--skill-gray-500, #8A8A8A);
      transition: transform var(--duration-fast, 200ms) var(--ease-out, cubic-bezier(0, 0, 0.2, 1));
      flex-shrink: 0;
    }

    .skill-collapse__item--expanded .skill-collapse__expand-icon {
      transform: rotate(180deg);
    }

    /* Content */
    .skill-collapse__content {
      overflow: hidden;
      transition: all var(--duration-normal, 300ms) var(--ease-out, cubic-bezier(0, 0, 0.2, 1));
      max-height: 0;
    }

    .skill-collapse__content-inner {
      padding: var(--spacing-md, 16px) var(--spacing-lg, 20px);
      color: var(--skill-gray-700, #5A5A5A);
      font-family: var(--font-sans, 'Inter', 'PingFang SC', sans-serif);
      font-size: var(--font-size-body-2, 14px);
      line-height: var(--line-height-normal, 1.5);
    }

    .skill-collapse__item--expanded .skill-collapse__content {
      max-height: 2000px; /* Large value to accommodate content */
    }

    /* Size variants */
    .skill-collapse--sm .skill-collapse__header {
      padding: var(--spacing-sm, 12px) var(--spacing-md, 16px);
      font-size: var(--font-size-body-3, 12px);
    }

    .skill-collapse--sm .skill-collapse__content-inner {
      padding: var(--spacing-sm, 12px) var(--spacing-md, 16px);
      font-size: var(--font-size-caption, 10px);
    }

    .skill-collapse--lg .skill-collapse__header {
      padding: var(--spacing-lg, 20px) var(--spacing-xl, 24px);
      font-size: var(--font-size-subtitle, 18px);
    }

    .skill-collapse--lg .skill-collapse__content-inner {
      padding: var(--spacing-lg, 20px) var(--spacing-xl, 24px);
      font-size: var(--font-size-body-1, 16px);
    }

    /* Icon position variants */
    .skill-collapse--icon-left .skill-collapse__header-content {
      order: 1;
    }

    .skill-collapse--icon-left .skill-collapse__header-actions {
      order: 0;
    }

    .skill-collapse--icon-left .skill-collapse__expand-icon {
      margin-right: var(--spacing-sm, 12px);
    }

    /* Accordion mode (only one item open at a time) */
    .skill-collapse--accordion .skill-collapse__item:not(.skill-collapse__item--expanded) .skill-collapse__header {
      background: var(--skill-gray-50, #FAFAFA);
    }

    .skill-collapse--accordion .skill-collapse__item--expanded .skill-collapse__header {
      background: var(--skill-primary-50, #E6F0FF);
      color: var(--skill-primary-700, #0036A0);
    }

    .skill-collapse--accordion .skill-collapse__item--expanded .skill-collapse__expand-icon {
      color: var(--skill-primary-600, #0047D3);
    }

    /* Borderless variant */
    .skill-collapse--borderless .skill-collapse__header {
      border-radius: var(--radius-md, 6px);
      margin-bottom: var(--spacing-xs, 8px);
    }

    .skill-collapse--borderless .skill-collapse__content {
      border-radius: var(--radius-md, 6px);
      background: var(--skill-gray-50, #FAFAFA);
    }

    /* Ghost variant */
    .skill-collapse--ghost .skill-collapse__header {
      border-radius: var(--radius-md, 6px);
      margin-bottom: var(--spacing-xs, 8px);
    }

    .skill-collapse--ghost .skill-collapse__item--expanded .skill-collapse__header {
      background: var(--skill-primary-50, #E6F0FF);
    }

    /* Extra icon in header */
    .skill-collapse__extra-icon {
      width: 16px;
      height: 16px;
      color: var(--skill-gray-400, #BDBDBD);
      flex-shrink: 0;
    }

    /* Loading state */
    .skill-collapse__loading {
      width: 16px;
      height: 16px;
      color: var(--skill-primary-500, #0A59F7);
      animation: spin 1s linear infinite;
    }

    @keyframes spin {
      from {
        transform: rotate(0deg);
      }
      to {
        transform: rotate(360deg);
      }
    }

    /* Arrow animation variants */
    .skill-collapse--arrow-none .skill-collapse__expand-icon {
      display: none;
    }

    .skill-collapse--arrow-chevron .skill-collapse__expand-icon {
      width: 16px;
      height: 16px;
    }

    .skill-collapse--arrow-plus .skill-collapse__expand-icon {
      width: 18px;
      height: 18px;
    }

    .skill-collapse--arrow-plus .skill-collapse__item--expanded .skill-collapse__expand-icon {
      transform: rotate(45deg);
    }

    /* Right-to-left support */
    :host([dir="rtl"]) .skill-collapse__expand-icon {
      transform: rotate(180deg);
    }

    :host([dir="rtl"]) .skill-collapse__item--expanded .skill-collapse__expand-icon {
      transform: rotate(0deg);
    }

    /* Accessibility */
    .skill-collapse__header:focus-visible {
      outline: 2px solid var(--skill-primary-500, #0A59F7);
      outline-offset: 2px;
    }

    /* CSS part exports */
    ::part(header) {
      /* Allows styling of the header button */
    }

    ::part(content) {
      /* Allows styling of the content container */
    }

    ::part(expand-icon) {
      /* Allows styling of the expand icon */
    }

    ::part(title) {
      /* Allows styling of the title */
    }

    ::part(extra) {
      /* Allows styling of extra content in header */
    }
  `,
]

css`
  :host {
    display: block;
  }

  .skill-input-wrapper {
    display: flex;
    flex-direction: column;
    gap: var(--skill-spacing-xs);
  }

  /* Label 样式 */
  .skill-input__label {
    display: block;
    font-size: var(--skill-font-body-3);
    font-weight: var(--skill-font-medium);
    color: var(--skill-gray-700);
    margin-bottom: var(--skill-spacing-2xs);
  }

  /* 输入框容器 */
  .skill-input__container {
    position: relative;
    display: flex;
    align-items: center;
  }

  /* 基础输入框样式 - 使用 mixin */
  input {
    ${inputBase}
  }

  /* 错误状态 */
  :host([error]) input {
    ${inputError}
  }

  /* 成功状态 */
  :host([success]) input {
    ${inputSuccess}
  }

  /* 警告状态 */
  :host([warning]) input {
    ${inputWarning}
  }

  /* 尺寸变体 */
  :host([size='xs']) input {
    ${inputSizeXS}
  }

  :host([size='sm']) input {
    ${inputSizeSM}
  }

  :host([size='lg']) input {
    ${inputSizeLG}
  }

  :host([size='xl']) input {
    ${inputSizeXL}
  }

  /* 前缀和后缀图标 */
  .skill-input__prefix,
  .skill-input__suffix {
    position: absolute;
    display: flex;
    align-items: center;
    color: var(--skill-gray-500);
    pointer-events: none;
  }

  .skill-input__prefix {
    left: var(--skill-spacing-md);
  }

  .skill-input__suffix {
    right: var(--skill-spacing-md);
  }

  .skill-input__prefix:empty,
  .skill-input__suffix:empty {
    display: none;
  }

  :host([has-prefix]) input {
    padding-left: calc(var(--skill-spacing-md) * 2.5);
  }

  :host([has-suffix]) input {
    padding-right: calc(var(--skill-spacing-md) * 2.5);
  }

  /* 辅助文本 */
  .skill-input__helper {
    font-size: var(--skill-font-body-3);
    color: var(--skill-gray-600);
    margin-top: var(--skill-spacing-2xs);
  }

  /* 错误消息 */
  .skill-input__error-message {
    font-size: var(--skill-font-body-3);
    color: var(--skill-error-500);
    margin-top: var(--skill-spacing-2xs);
  }

  /* Disabled 状态 */
  input:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`

css`
  :host {
    display: inline-block;
    width: 100%;
  }

  .skill-progress {
    position: relative;
    display: flex;
    align-items: center;
    gap: var(--skill-spacing-sm, 12px);
  }

  /* 线性进度条基础样式 */
  .skill-progress__bar {
    flex: 1;
    height: var(--progress-height, 8px);
    background: var(--skill-gray-200, #E5E8EB);
    border-radius: var(--progress-radius, var(--skill-radius-full, 9999px));
    overflow: hidden;
    position: relative;
    transition: all var(--skill-duration-normal, 300ms) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  .skill-progress__fill {
    height: 100%;
    background: var(--progress-color, var(--skill-primary-500, #0A59F7));
    border-radius: inherit;
    transition: width var(--progress-duration, var(--skill-duration-normal, 300ms)) var(--progress-ease, var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1)));
    position: relative;
    overflow: hidden;
  }

  /* 进度条条纹效果 */
  :host([striped]) .skill-progress__fill {
    background-image: linear-gradient(
      45deg,
      rgba(255, 255, 255, 0.15) 25%,
      transparent 25%,
      transparent 50%,
      rgba(255, 255, 255, 0.15) 50%,
      rgba(255, 255, 255, 0.15) 75%,
      transparent 75%,
      transparent
    );
    background-size: var(--stripe-size, 1rem) var(--stripe-size, 1rem);
  }

  /* 动画条纹效果 */
  :host([animated]) .skill-progress__fill {
    animation: progress-stripes 1s linear infinite;
  }

  @keyframes progress-stripes {
    0% {
      background-position: 0 0;
    }
    100% {
      background-position: var(--stripe-size, 1rem) var(--stripe-size, 1rem);
    }
  }

  /* 圆形进度条 */
  :host([type='circular']) .skill-progress {
    display: inline-block;
    position: relative;
    width: var(--progress-size, 120px);
    height: var(--progress-size, 120px);
  }

  :host([type='circular']) .skill-progress__circular {
    position: relative;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  :host([type='circular']) .skill-progress__circle {
    position: absolute;
    top: 0;
    left: 0;
    transform: rotate(-90deg);
  }

  :host([type='circular']) .skill-progress__circle-bg {
    fill: none;
    stroke: var(--skill-gray-200, #E5E8EB);
    stroke-width: var(--stroke-width, 8);
  }

  :host([type='circular']) .skill-progress__circle-progress {
    fill: none;
    stroke: var(--progress-color, var(--skill-primary-500, #0A59F7));
    stroke-width: var(--stroke-width, 8);
    stroke-linecap: round;
    transition: stroke-dashoffset var(--progress-duration, var(--skill-duration-normal, 300ms)) var(--progress-ease, var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  :host([type='circular']) .skill-progress__content {
    position: relative;
    z-index: 1;
    font-size: var(--content-font-size, var(--skill-font-size-title-3, 20px));
    font-weight: var(--content-font-weight, var(--skill-font-weight-semibold, 600));
    color: var(--content-color, var(--skill-gray-900, #1A1A1A));
  }

  /* 尺寸变体 */
  :host([size='xs']) {
    --progress-height: 4px;
    --content-font-size: var(--skill-font-size-caption, 10px);
  }

  :host([size='sm']) {
    --progress-height: 6px;
    --content-font-size: var(--skill-font-size-body-3, 12px);
  }

  :host([size='md']) {
    --progress-height: 8px;
    --content-font-size: var(--skill-font-size-body-2, 14px);
  }

  :host([size='lg']) {
    --progress-height: 12px;
    --content-font-size: var(--skill-font-size-body-1, 16px);
  }

  :host([size='xl']) {
    --progress-height: 16px;
    --content-font-size: var(--skill-font-size-title-3, 20px);
  }

  :host([type='circular'][size='xs']) {
    --progress-size: 60px;
    --stroke-width: 4;
    --content-font-size: var(--skill-font-size-body-3, 12px);
  }

  :host([type='circular'][size='sm']) {
    --progress-size: 80px;
    --stroke-width: 6;
    --content-font-size: var(--skill-font-size-body-2, 14px);
  }

  :host([type='circular'][size='md']) {
    --progress-size: 120px;
    --stroke-width: 8;
    --content-font-size: var(--skill-font-size-title-3, 20px);
  }

  :host([type='circular'][size='lg']) {
    --progress-size: 160px;
    --stroke-width: 12;
    --content-font-size: var(--skill-font-size-title-2, 24px);
  }

  :host([type='circular'][size='xl']) {
    --progress-size: 200px;
    --stroke-width: 16;
    --content-font-size: var(--skill-font-size-title-1, 28px);
  }

  /* 颜色变体 */
  :host([color='primary']) {
    --progress-color: var(--skill-primary-500, #0A59F7);
  }

  :host([color='secondary']) {
    --progress-color: var(--skill-secondary-500, #00D4AA);
  }

  :host([color='success']) {
    --progress-color: var(--skill-success-500, #00D4AA);
  }

  :host([color='warning']) {
    --progress-color: var(--skill-warning-500, #FFB400);
  }

  :host([color='error']) {
    --progress-color: var(--skill-error-500, #FA2A2D);
  }

  :host([color='info']) {
    --progress-color: var(--skill-info-500, #0A59F7);
  }

  /* 状态变体 */
  :host([status='loading']) .skill-progress__fill {
    position: relative;
    overflow: hidden;
  }

  :host([status='loading']) .skill-progress__fill::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(255, 255, 255, 0.3),
      transparent
    );
    animation: progress-loading 1.5s infinite;
  }

  @keyframes progress-loading {
    0% {
      transform: translateX(-100%);
    }
    100% {
      transform: translateX(100%);
    }
  }

  :host([status='success']) {
    --progress-color: var(--skill-success-500, #00D4AA);
  }

  :host([status='error']) {
    --progress-color: var(--skill-error-500, #FA2A2D);
  }

  :host([status='warning']) {
    --progress-color: var(--skill-warning-500, #FFB400);
  }

  /* 标签样式 */
  .skill-progress__label {
    font-size: var(--label-font-size, var(--skill-font-size-body-2, 14px));
    font-weight: var(--label-font-weight, var(--skill-font-weight-medium, 500));
    color: var(--label-color, var(--skill-gray-700, #5A5A5A));
    white-space: nowrap;
  }

  .skill-progress__value {
    font-size: var(--value-font-size, var(--skill-font-size-body-2, 14px));
    font-weight: var(--value-font-weight, var(--skill-font-weight-medium, 500));
    color: var(--value-color, var(--skill-gray-700, #5A5A5A));
    min-width: var(--value-min-width, 45px);
    text-align: right;
  }

  /* 标签位置 */
  :host([label-position='top']) .skill-progress {
    flex-direction: column;
    align-items: stretch;
  }

  :host([label-position='top']) .skill-progress__label {
    margin-bottom: var(--skill-spacing-xs, 8px);
    text-align: left;
  }

  :host([label-position='bottom']) .skill-progress {
    flex-direction: column;
    align-items: stretch;
  }

  :host([label-position='bottom']) .skill-progress__label {
    margin-top: var(--skill-spacing-xs, 8px);
    text-align: left;
  }

  :host([label-position='hidden']) .skill-progress__label {
    display: none;
  }

  :host([value-position='left']) .skill-progress__value {
    order: -1;
    text-align: left;
  }

  :host([value-position='hidden']) .skill-progress__value {
    display: none;
  }

  /* 圆形进度条标签 */
  :host([type='circular'][label-position='top']) .skill-progress {
    flex-direction: column;
  }

  :host([type='circular'][label-position='top']) .skill-progress__label {
    margin-bottom: var(--skill-spacing-sm, 12px);
    text-align: center;
  }

  :host([type='circular'][label-position='bottom']) .skill-progress {
    flex-direction: column;
  }

  :host([type='circular'][label-position='bottom']) .skill-progress__label {
    margin-top: var(--skill-spacing-sm, 12px);
    text-align: center;
  }

  /* 缓动函数变体 */
  :host([easing='linear']) {
    --progress-ease: linear;
  }

  :host([easing='ease-in']) {
    --progress-ease: var(--skill-ease-in, cubic-bezier(0.4, 0, 1, 1));
  }

  :host([easing='ease-out']) {
    --progress-ease: var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  :host([easing='ease-in-out']) {
    --progress-ease: var(--skill-ease-in-out, cubic-bezier(0.4, 0, 0.2, 1));
  }

  :host([easing='bounce']) {
    --progress-ease: var(--skill-ease-bounce, cubic-bezier(0.68, -0.55, 0.265, 1.55));
  }

  :host([easing='spring']) {
    --progress-ease: var(--skill-ease-spring, cubic-bezier(0.68, -0.55, 0.265, 1.55));
  }

  /* 动画时长变体 */
  :host([duration='instant']) {
    --progress-duration: var(--skill-duration-instant, 100ms);
  }

  :host([duration='fast']) {
    --progress-duration: var(--skill-duration-fast, 200ms);
  }

  :host([duration='normal']) {
    --progress-duration: var(--skill-duration-normal, 300ms);
  }

  :host([duration='slow']) {
    --progress-duration: var(--skill-duration-slow, 500ms);
  }

  :host([duration='slower']) {
    --progress-duration: var(--skill-duration-slower, 800ms);
  }

  /* 无障碍支持 */
  :host([indeterminate]) .skill-progress__fill {
    width: 30%;
    animation: progress-indeterminate 2s infinite ease-in-out;
  }

  @keyframes progress-indeterminate {
    0% {
      left: -30%;
    }
    50% {
      left: 100%;
    }
    100% {
      left: 100%;
    }
  }

  :host([indeterminate]) .skill-progress__fill {
    position: absolute;
    left: -30%;
    top: 0;
  }

  /* 响应式设计 */
  @media (max-width: 768px) {
    :host([responsive]) .skill-progress__label {
      font-size: var(--skill-font-size-body-3, 12px);
    }

    :host([responsive]) .skill-progress__value {
      font-size: var(--skill-font-size-body-3, 12px);
    }
  }

  /* 高对比度模式支持 */
  @media (prefers-contrast: high) {
    .skill-progress__bar {
      border: 1px solid var(--skill-gray-600, #6B7280);
    }

    .skill-progress__fill {
      border: 1px solid var(--skill-gray-900, #1A1A1A);
    }
  }

  /* 减少动画模式支持 */
  @media (prefers-reduced-motion: reduce) {
    .skill-progress__fill,
    .skill-progress__circle-progress {
      transition: none;
    }

    .skill-progress__fill::after,
    .skill-progress__circle-progress::after {
      animation: none;
    }

    :host([striped][animated]) .skill-progress__fill {
      animation: none;
    }
  }

  /* 自定义CSS变量支持 */
  .skill-progress {
    --progress-height: 8px;
    --progress-radius: var(--skill-radius-full, 9999px);
    --progress-size: 120px;
    --stroke-width: 8;
    --stripe-size: 1rem;
    --progress-duration: var(--skill-duration-normal, 300ms);
    --progress-ease: var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
    --progress-color: var(--skill-primary-500, #0A59F7);
    --label-font-size: var(--skill-font-size-body-2, 14px);
    --label-font-weight: var(--skill-font-weight-medium, 500);
    --label-color: var(--skill-gray-700, #5A5A5A);
    --value-font-size: var(--skill-font-size-body-2, 14px);
    --value-font-weight: var(--skill-font-weight-medium, 500);
    --value-color: var(--skill-gray-700, #5A5A5A);
    --value-min-width: 45px;
    --content-font-size: var(--skill-font-size-title-3, 20px);
    --content-font-weight: var(--skill-font-weight-semibold, 600);
    --content-color: var(--skill-gray-900, #1A1A1A);
  }
`

css`
  :host {
    display: inline-block;
    --skill-radio-size: var(--skill-component-size-md, 20px);
    --skill-radio-border-width: var(--skill-border-width-md, 2px);
    --skill-radio-transition: var(--skill-transition-fast, 0.2s);
    --skill-radio-check-size: var(--skill-component-size-sm, 12px);

    /* Color variables */
    --skill-radio-bg-color: var(--skill-white, #ffffff);
    --skill-radio-border-color: var(--skill-gray-300, #e1e5e9);
    --skill-radio-border-color-hover: var(--skill-primary-600, #0056b3);
    --skill-radio-border-color-focus: var(--skill-primary-500, #0066cc);
    --skill-radio-border-color-checked: var(--skill-primary-500, #0066cc);
    --skill-radio-check-color: var(--skill-primary-500, #0066cc);
    --skill-radio-disabled-bg-color: var(--skill-gray-100, #f8f9fa);
    --skill-radio-disabled-border-color: var(--skill-gray-200, #dee2e6);
    --skill-radio-disabled-check-color: var(--skill-gray-400, #6c757d);
    --skill-radio-error-border-color: var(--skill-error-500, #dc3545);

    /* Label colors */
    --skill-radio-label-color: var(--skill-gray-900, #212529);
    --skill-radio-label-color-disabled: var(--skill-gray-500, #6c757d);

    /* Helper text colors */
    --skill-radio-helper-text-color: var(--skill-gray-600, #6c757d);
    --skill-radio-error-text-color: var(--skill-error-500, #dc3545);
  }

  /* Size variants */
  :host([size='xs']) {
    --skill-radio-size: var(--skill-component-size-xs, 14px);
    --skill-radio-check-size: var(--skill-component-size-xs, 8px);
  }

  :host([size='sm']) {
    --skill-radio-size: var(--skill-component-size-sm, 16px);
    --skill-radio-check-size: var(--skill-component-size-xs, 10px);
  }

  :host([size='lg']) {
    --skill-radio-size: var(--skill-component-size-lg, 24px);
    --skill-radio-check-size: var(--skill-component-size-md, 16px);
  }

  :host([size='xl']) {
    --skill-radio-size: var(--skill-component-size-xl, 28px);
    --skill-radio-check-size: var(--skill-component-size-lg, 18px);
  }

  /* Color variants */
  :host([color='primary']) {
    --skill-radio-border-color-hover: var(--skill-primary-600, #0056b3);
    --skill-radio-border-color-focus: var(--skill-primary-500, #0066cc);
    --skill-radio-border-color-checked: var(--skill-primary-500, #0066cc);
    --skill-radio-check-color: var(--skill-primary-500, #0066cc);
  }

  :host([color='secondary']) {
    --skill-radio-border-color-hover: var(--skill-gray-600, #5c636a);
    --skill-radio-border-color-focus: var(--skill-gray-500, #6c757d);
    --skill-radio-border-color-checked: var(--skill-gray-500, #6c757d);
    --skill-radio-check-color: var(--skill-gray-500, #6c757d);
  }

  :host([color='success']) {
    --skill-radio-border-color-hover: var(--skill-success-600, #218838);
    --skill-radio-border-color-focus: var(--skill-success-500, #28a745);
    --skill-radio-border-color-checked: var(--skill-success-500, #28a745);
    --skill-radio-check-color: var(--skill-success-500, #28a745);
  }

  :host([color='warning']) {
    --skill-radio-border-color-hover: var(--skill-warning-600, #e0a800);
    --skill-radio-border-color-focus: var(--skill-warning-500, #ffc107);
    --skill-radio-border-color-checked: var(--skill-warning-500, #ffc107);
    --skill-radio-check-color: var(--skill-warning-500, #ffc107);
  }

  :host([color='error']) {
    --skill-radio-border-color-hover: var(--skill-error-600, #c82333);
    --skill-radio-border-color-focus: var(--skill-error-500, #dc3545);
    --skill-radio-border-color-checked: var(--skill-error-500, #dc3545);
    --skill-radio-check-color: var(--skill-error-500, #dc3545);
  }

  :host([color='info']) {
    --skill-radio-border-color-hover: var(--skill-info-600, #138496);
    --skill-radio-border-color-focus: var(--skill-info-500, #17a2b8);
    --skill-radio-border-color-checked: var(--skill-info-500, #17a2b8);
    --skill-radio-check-color: var(--skill-info-500, #17a2b8);
  }

  /* Main radio container */
  .skill-radio {
    display: inline-flex;
    align-items: flex-start;
    gap: var(--skill-spacing-sm, 8px);
    cursor: pointer;
    user-select: none;
    position: relative;
    margin: 0;
    font-family: var(--skill-font-family, inherit);
    color: var(--skill-radio-label-color);
    transition: color var(--skill-radio-transition);
  }

  .skill-radio:disabled {
    cursor: not-allowed;
    color: var(--skill-radio-label-color-disabled);
  }

  .skill-radio:hover:not(:disabled) {
    color: var(--skill-radio-label-color);
  }

  /* Hidden input */
  .skill-radio__input {
    position: absolute;
    opacity: 0;
    width: 0;
    height: 0;
    margin: 0;
    padding: 0;
    border: none;
    outline: none;
    pointer-events: none;
  }

  .skill-radio__input:focus-visible + .skill-radio__control {
    box-shadow: 0 0 0 2px var(--skill-white, #ffffff),
                0 0 0 4px var(--skill-radio-border-color-focus);
    outline: none;
  }

  :host([has-error]) .skill-radio__input:focus-visible + .skill-radio__control,
  :host([error]) .skill-radio__input:focus-visible + .skill-radio__control {
    box-shadow: 0 0 0 2px var(--skill-white, #ffffff),
                0 0 0 4px var(--skill-radio-error-border-color);
  }

  /* Radio control */
  .skill-radio__control {
    position: relative;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: var(--skill-radio-size);
    height: var(--skill-radio-size);
    border: var(--skill-radio-border-width) solid var(--skill-radio-border-color);
    border-radius: 50%; /* Circle for radio */
    background-color: var(--skill-radio-bg-color);
    transition: all var(--skill-radio-transition);
    flex-shrink: 0;
    margin-top: 2px; /* Align with text */
  }

  .skill-radio:hover:not(:disabled) .skill-radio__control {
    border-color: var(--skill-radio-border-color-hover);
  }

  :host([has-error]) .skill-radio__control,
  :host([error]) .skill-radio__control {
    border-color: var(--skill-radio-error-border-color);
  }

  :host(:disabled) .skill-radio__control {
    background-color: var(--skill-radio-disabled-bg-color);
    border-color: var(--skill-radio-disabled-border-color);
  }

  /* Checked state */
  :host([checked]) .skill-radio__control {
    border-color: var(--skill-radio-border-color-checked);
  }

  :host([checked]:disabled) .skill-radio__control {
    background-color: var(--skill-radio-disabled-bg-color);
    border-color: var(--skill-radio-disabled-border-color);
  }

  /* Variant styles */
  :host([variant='filled']) .skill-radio__control {
    background-color: var(--skill-gray-50, #f8f9fa);
  }

  :host([variant='outlined']) .skill-radio__control {
    border-width: var(--skill-border-width-lg, 3px);
  }

  /* Radio check indicator */
  .skill-radio__check {
    width: var(--skill-radio-check-size);
    height: var(--skill-radio-check-size);
    border-radius: 50%;
    background-color: var(--skill-radio-check-color);
    opacity: 0;
    transform: scale(0);
    transition: all var(--skill-radio-transition);
  }

  :host([checked]) .skill-radio__check {
    opacity: 1;
    transform: scale(1);
  }

  :host(:disabled) .skill-radio__check {
    background-color: var(--skill-radio-disabled-check-color);
  }

  /* Label */
  .skill-radio__label {
    font-size: var(--skill-font-body-3, 14px);
    line-height: var(--skill-line-height-md, 1.5);
    font-weight: var(--skill-font-normal, 400);
    color: inherit;
    flex: 1;
  }

  /* Helper text container */
  .skill-radio__helper {
    margin-top: var(--skill-spacing-xs, 4px);
    padding-left: calc(var(--skill-radio-size) + var(--skill-spacing-sm, 8px));
  }

  .skill-radio__helper-text {
    font-size: var(--skill-font-body-4, 12px);
    line-height: var(--skill-line-height-sm, 1.4);
    color: var(--skill-radio-helper-text-color);
    display: block;
  }

  .skill-radio__error {
    font-size: var(--skill-font-body-4, 12px);
    line-height: var(--skill-line-height-sm, 1.4);
    color: var(--skill-radio-error-text-color);
    display: block;
  }

  /* Animation for radio check */
  @keyframes radioCheck {
    0% {
      opacity: 0;
      transform: scale(0);
    }
    50% {
      transform: scale(1.2);
    }
    100% {
      opacity: 1;
      transform: scale(1);
    }
  }

  :host([checked]) .skill-radio__check {
    animation: radioCheck 0.25s cubic-bezier(0.4, 0, 0.2, 1);
  }

  /* Focus styles for high contrast mode */
  @media (prefers-contrast: high) {
    .skill-radio__control {
      border-width: 3px;
    }

    .skill-radio__input:focus-visible + .skill-radio__control {
      box-shadow: 0 0 0 2px var(--skill-white, #ffffff),
                  0 0 0 4px currentColor;
    }
  }

  /* Reduced motion */
  @media (prefers-reduced-motion: reduce) {
    .skill-radio,
    .skill-radio__control,
    .skill-radio__check {
      transition: none;
    }

    :host([checked]) .skill-radio__check {
      animation: none;
    }
  }

  /* Dark mode support */
  @media (prefers-color-scheme: dark) {
    :host {
      --skill-radio-bg-color: var(--skill-gray-800, #1a1a1a);
      --skill-radio-border-color: var(--skill-gray-600, #6c757d);
      --skill-radio-label-color: var(--skill-gray-200, #e9ecef);
      --skill-radio-helper-text-color: var(--skill-gray-400, #adb5bd);
      --skill-radio-disabled-bg-color: var(--skill-gray-900, #212529);
      --skill-radio-disabled-border-color: var(--skill-gray-700, #495057);
      --skill-radio-disabled-check-color: var(--skill-gray-500, #6c757d);
    }

    :host([variant='filled']) .skill-radio__control {
      background-color: var(--skill-gray-700, #495057);
    }
  }
`

css`
  :host {
    display: inline-block;
    width: 100%;
  }

  .skill-skeleton {
    display: block;
    background: linear-gradient(
      90deg,
      var(--skill-gray-200, #E5E8EB) 25%,
      var(--skill-gray-100, #F1F3F5) 50%,
      var(--skill-gray-200, #E5E8EB) 75%
    );
    background-size: 200% 100%;
    border-radius: var(--skill-radius-base, 4px);
    animation: skeleton-loading 1.5s infinite ease-in-out;
    transition: all var(--skill-duration-normal, 300ms) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  /* 骨架屏动画 */
  @keyframes skeleton-loading {
    0% {
      background-position: 200% 0;
    }
    100% {
      background-position: -200% 0;
    }
  }

  /* 脉冲动画变体 */
  :host([animation='pulse']) .skill-skeleton {
    animation: skeleton-pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes skeleton-pulse {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: 0.5;
    }
  }

  /* 波浪动画变体 */
  :host([animation='wave']) .skill-skeleton {
    position: relative;
    overflow: hidden;
    background: var(--skill-gray-200, #E5E8EB);
  }

  :host([animation='wave']) .skill-skeleton::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(255, 255, 255, 0.4),
      transparent
    );
    transform: translateX(-100%);
    animation: skeleton-wave 1.5s infinite ease-out;
  }

  @keyframes skeleton-wave {
    0% {
      transform: translateX(-100%);
    }
    100% {
      transform: translateX(100%);
    }
  }

  /* 淡入淡出动画变体 */
  :host([animation='fade']) .skill-skeleton {
    animation: skeleton-fade 1.5s infinite ease-in-out;
  }

  @keyframes skeleton-fade {
    0%, 100% {
      opacity: 0.6;
    }
    50% {
      opacity: 1;
    }
  }

  /* 无动画变体 */
  :host([animation='none']) .skill-skeleton {
    animation: none;
    background: var(--skill-gray-200, #E5E8EB);
  }

  /* 基础形状 */
  :host([shape='text']) .skill-skeleton {
    height: 1em;
    border-radius: var(--skill-radius-xs, 2px);
    max-width: 100%;
  }

  :host([shape='title']) .skill-skeleton {
    height: 1.5em;
    border-radius: var(--skill-radius-sm, 4px);
    width: 60%;
  }

  :host([shape='paragraph']) .skill-skeleton {
    height: 1em;
    border-radius: var(--skill-radius-xs, 2px);
    margin-bottom: 0.5em;
  }

  :host([shape='paragraph']) .skill-skeleton:last-child {
    margin-bottom: 0;
  }

  :host([shape='avatar']) .skill-skeleton {
    width: 40px;
    height: 40px;
    border-radius: var(--skill-radius-full, 9999px);
  }

  :host([shape='circle']) .skill-skeleton {
    border-radius: var(--skill-radius-full, 9999px);
  }

  :host([shape='square']) .skill-skeleton {
    border-radius: var(--skill-radius-xs, 2px);
  }

  :host([shape='rounded']) .skill-skeleton {
    border-radius: var(--skill-radius-lg, 8px);
  }

  /* 尺寸变体 */
  :host([size='xs']) .skill-skeleton {
    width: 60px;
    height: 12px;
  }

  :host([size='sm']) .skill-skeleton {
    width: 80px;
    height: 16px;
  }

  :host([size='md']) .skill-skeleton {
    width: 120px;
    height: 20px;
  }

  :host([size='lg']) .skill-skeleton {
    width: 180px;
    height: 24px;
  }

  :host([size='xl']) .skill-skeleton {
    width: 240px;
    height: 32px;
  }

  /* 自定义尺寸 */
  :host([width]) .skill-skeleton {
    width: var(--skeleton-width, auto);
  }

  :host([height]) .skill-skeleton {
    height: var(--skeleton-height, auto);
  }

  /* 按钮骨架屏 */
  :host([shape='button']) .skill-skeleton {
    height: 40px;
    border-radius: var(--skill-radius-md, 6px);
    padding: 0 16px;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    min-width: 80px;
  }

  /* 输入框骨架屏 */
  :host([shape='input']) .skill-skeleton {
    height: 40px;
    border-radius: var(--skill-radius-md, 6px);
    border: 1px solid var(--skill-gray-300, #D1D5DB);
  }

  /* 卡片骨架屏 */
  :host([shape='card']) .skill-skeleton {
    border-radius: var(--skill-radius-lg, 8px);
    padding: 16px;
    min-height: 120px;
  }

  /* 图片骨架屏 */
  :host([shape='image']) .skill-skeleton {
    border-radius: var(--skill-radius-base, 4px);
    background: var(--skill-gray-100, #F1F3F5);
    position: relative;
  }

  :host([shape='image']) .skill-skeleton::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 24px;
    height: 24px;
    border: 2px solid var(--skill-gray-300, #D1D5DB);
    border-radius: 50%;
    border-top-color: var(--skill-gray-400, #BDBDBD);
    animation: skeleton-image-spin 1s linear infinite;
  }

  @keyframes skeleton-image-spin {
    0% {
      transform: translate(-50%, -50%) rotate(0deg);
    }
    100% {
      transform: translate(-50%, -50%) rotate(360deg);
    }
  }

  /* 表格行骨架屏 */
  :host([shape='table-row']) .skill-skeleton {
    display: flex;
    gap: 16px;
    padding: 12px 0;
    border-bottom: 1px solid var(--skill-gray-200, #E5E8EB);
  }

  :host([shape='table-row']) .skill-skeleton::before,
  :host([shape='table-row']) .skill-skeleton::after {
    content: '';
    flex: 1;
    height: 16px;
    background: inherit;
    border-radius: inherit;
    animation: inherit;
  }

  :host([shape='table-row']) .skill-skeleton::after {
    flex: 2;
  }

  /* 列表项骨架屏 */
  :host([shape='list-item']) .skill-skeleton {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 8px 0;
  }

  :host([shape='list-item']) .skill-skeleton::before {
    content: '';
    width: 32px;
    height: 32px;
    border-radius: var(--skill-radius-full, 9999px);
    background: inherit;
    animation: inherit;
    flex-shrink: 0;
  }

  :host([shape='list-item']) .skill-skeleton::after {
    content: '';
    flex: 1;
    height: 16px;
    background: inherit;
    border-radius: inherit;
    animation: inherit;
  }

  /* 代码块骨架屏 */
  :host([shape='code']) .skill-skeleton {
    font-family: var(--skill-font-family-mono, 'JetBrains Mono', monospace);
    background: var(--skill-gray-900, #1A1A1A) !important;
    border-radius: var(--skill-radius-md, 6px);
    padding: 16px;
    color: var(--skill-gray-600, #6B7280);
  }

  :host([shape='code']) .skill-skeleton::after {
    content: '// Loading code...';
    font-size: 14px;
    opacity: 0.5;
  }

  /* 图表骨架屏 */
  :host([shape='chart']) .skill-skeleton {
    min-height: 200px;
    background: var(--skill-gray-50, #FAFAFA) !important;
    border: 1px dashed var(--skill-gray-300, #D1D5DB);
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  :host([shape='chart']) .skill-skeleton::after {
    content: '📊 Loading chart...';
    font-size: 16px;
    color: var(--skill-gray-400, #BDBDBD);
  }

  /* 深色主题 */
  :host([theme='dark']) .skill-skeleton {
    background: linear-gradient(
      90deg,
      var(--skill-gray-700, #5A5A5A) 25%,
      var(--skill-gray-600, #6B7280) 50%,
      var(--skill-gray-700, #5A5A5A) 75%
    );
    background-size: 200% 100%;
  }

  :host([theme='dark'][animation='pulse']) .skill-skeleton {
    background: var(--skill-gray-700, #5A5A5A);
  }

  :host([theme='dark'][animation='wave']) .skill-skeleton {
    background: var(--skill-gray-700, #5A5A5A);
  }

  :host([theme='dark'][animation='fade']) .skill-skeleton {
    background: var(--skill-gray-700, #5A5A5A);
  }

  :host([theme='dark'][animation='none']) .skill-skeleton {
    background: var(--skill-gray-700, #5A5A5A);
  }

  /* 圆角变体 */
  :host([radius='none']) .skill-skeleton {
    border-radius: 0;
  }

  :host([radius='xs']) .skill-skeleton {
    border-radius: var(--skill-radius-xs, 2px);
  }

  :host([radius='sm']) .skill-skeleton {
    border-radius: var(--skill-radius-sm, 4px);
  }

  :host([radius='md']) .skill-skeleton {
    border-radius: var(--skill-radius-md, 6px);
  }

  :host([radius='lg']) .skill-skeleton {
    border-radius: var(--skill-radius-lg, 8px);
  }

  :host([radius='xl']) .skill-skeleton {
    border-radius: var(--skill-radius-xl, 12px);
  }

  :host([radius='full']) .skill-skeleton {
    border-radius: var(--skill-radius-full, 9999px);
  }

  /* 响应式设计 */
  @media (max-width: 768px) {
    :host([responsive]) .skill-skeleton {
      max-width: 100%;
    }
  }

  /* 加载状态过渡 */
  :host([loading]) .skill-skeleton {
    opacity: 1;
  }

  :host(:not([loading])) .skill-skeleton {
    opacity: 0;
    transition: opacity var(--skill-duration-fast, 200ms) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  /* 高对比度模式支持 */
  @media (prefers-contrast: high) {
    .skill-skeleton {
      background: var(--skill-gray-400, #BDBDBD);
      animation: none;
    }
  }

  /* 减少动画模式支持 */
  @media (prefers-reduced-motion: reduce) {
    .skill-skeleton {
      animation: none;
    }
  }

  /* 自定义CSS变量支持 */
  .skill-skeleton {
    --skeleton-color-start: var(--skill-gray-200, #E5E8EB);
    --skeleton-color-middle: var(--skill-gray-100, #F1F3F5);
    --skeleton-color-end: var(--skill-gray-200, #E5E8EB);
    --skeleton-animation-duration: 1.5s;
    --skeleton-radius: var(--skill-radius-base, 4px);
  }

  :host(:not([animation='pulse']):not([animation='wave']):not([animation='fade']):not([animation='none'])) .skill-skeleton {
    background: linear-gradient(
      90deg,
      var(--skeleton-color-start) 25%,
      var(--skeleton-color-middle) 50%,
      var(--skeleton-color-end) 75%
    );
    background-size: 200% 100%;
    border-radius: var(--skeleton-radius);
    animation-duration: var(--skeleton-animation-duration);
  }
`

css`
  :host {
    display: inline;
  }

  .skill-link {
    display: inline-flex;
    align-items: center;
    gap: var(--link-gap, var(--skill-spacing-2xs, 4px));
    color: var(--link-color, var(--skill-primary-600, #2563EB));
    text-decoration: var(--link-decoration, none);
    font-weight: var(--link-font-weight, var(--skill-font-weight-medium, 500));
    font-size: var(--link-font-size, var(--skill-font-size-body-2, 14px));
    line-height: var(--link-line-height, 1.4);
    cursor: pointer;
    transition: all var(--skill-duration-fast, 200ms) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
    border: none;
    background: none;
    padding: 0;
    margin: 0;
    outline: none;
    max-width: var(--link-max-width, none);
    border-radius: var(--skill-radius-sm, 4px);
    word-break: break-word;
  }

  .skill-link:focus-visible {
    outline: 2px solid var(--skill-primary-500, #0A59F7);
    outline-offset: 2px;
    border-radius: var(--skill-radius-sm, 4px);
  }

  .skill-link:hover {
    color: var(--link-hover-color, var(--skill-primary-700, #1D4ED8));
  }

  .skill-link:active {
    color: var(--link-active-color, var(--skill-primary-800, #1E40AF));
  }

  .skill-link:visited {
    color: var(--link-visited-color, var(--skill-purple-600, #9333EA));
  }

  .skill-link__icon {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
  }

  .skill-link__content {
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
