  :host([color='info']) .skill-link {
    --link-color: var(--skill-info-600, #0284C7);
    --link-hover-color: var(--skill-info-700, #0369A1);
    --link-active-color: var(--skill-info-800, #075985);
  }

  :host([color='gray']) .skill-link {
