  :host([router-link]) .skill-link {
    text-decoration: none;
  }

  /* 下载链接指示器 */
  :host([download]) .skill-link::before {
    content: "↓";
    display: inline-flex;
    align-items: center;
    margin-right: 4px;
    font-size: 0.9em;
    opacity: 0.7;
  }

  /* 邮件链接样式 */
  :host([href^="mailto:"]) .skill-link::before {
    content: "✉";
    display: inline-flex;
    align-items: center;
    margin-right: 4px;
    font-size: 0.9em;
    opacity: 0.7;
  }

  /* 电话链接样式 */
  :host([href^="tel:"]) .skill-link::before {
    content: "📞";
    display: inline-flex;
    align-items: center;
    margin-right: 4px;
    font-size: 0.9em;
    opacity: 0.7;
  }

  /* 锚点链接样式 */
  :host([href^="#"]) .skill-link::before {
    content: "⚓";
    display: inline-flex;
    align-items: center;
    margin-right: 4px;
    font-size: 0.9em;
    opacity: 0.7;
  }

  /* 响应式设计 */
  @media (max-width: 768px) {
    :host([responsive]) .skill-link {
      --link-font-size: 13px;
      --link-gap: 2px;
    }
  }

  /* 高对比度模式支持 */
  @media (prefers-contrast: high) {
    .skill-link {
      --link-font-weight: var(--skill-font-weight-semibold, 600);
      text-decoration: underline;
      text-underline-offset: 4px;
    }

    .skill-link:focus-visible {
      outline-width: 3px;
    }
  }

  /* 减少动画偏好支持 */
  @media (prefers-reduced-motion: reduce) {
    .skill-link,
    .skill-link__external-icon {
      transition: none;
    }
  }

  /* 打印样式 */
  @media print {
    .skill-link {
      color: #000;
      text-decoration: underline;
    }

    .skill-link::after {
      content: " (" attr(href) ")";
      font-size: 0.8em;
      color: #666;
    }

    .skill-link[href^="#"]::after,
    .skill-link[href^="javascript:"]::after {
      content: "";
    }
  }

  /* 焦点指示器增强 */
  @media (prefers-reduced-motion: no-preference) {
    .skill-link:focus-visible {
      animation: link-focus 0.2s ease-out;
    }
  }

  @keyframes link-focus {
    0% {
      box-shadow: 0 0 0 0 rgba(10, 89, 247, 0.4);
    }
    100% {
      box-shadow: 0 0 0 4px rgba(10, 89, 247, 0.1);
    }
  }

  /* 深色模式支持 */
  @media (prefers-color-scheme: dark) {
    :host([color='gray']) .skill-link {
      --link-color: var(--skill-gray-400, #9CA3AF);
      --link-hover-color: var(--skill-gray-300, #D1D5DB);
