  @media (prefers-contrast: high) {
    .skill-slider__track {
      border: 1px solid var(--skill-gray-500, #8A8A8A);
    }

    .skill-slider__handle {
      border-width: 3px;
    }

    .skill-slider__mark {
      width: 3px;
      height: 3px;
    }
  }

  /* ��;!/ */
  @media (prefers-reduced-motion: reduce) {
    .skill-slider__track-fill,
    .skill-slider__handle,
    .skill-slider__tooltip {
      transition: none;
    }
  }

  /* �ICSS��/ */
  .skill-slider {
    --slider-height: var(--skill-component-height-sm, 32px);
    --slider-width: 200px;
    --track-height: 6px;
    --track-radius: var(--skill-radius-full, 9999px);
    --track-bg: var(--skill-gray-200, #E5E8EB);
    --track-fill-bg: var(--skill-primary-500, #0A59F7);
    --track-fill-transition: width var(--slider-duration, var(--skill-duration-fast, 200ms)) var(--slider-ease, var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1)));
    --handle-size: 20px;
    --handle-radius: var(--skill-radius-full, 9999px);
    --handle-bg: var(--skill-white, #FFFFFF);
    --handle-border-width: 2px;
    --handle-border-color: var(--skill-primary-500, #0A59F7);
    --handle-shadow: var(--skill-shadow-md, 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)));
    --handle-hover-shadow: var(--skill-shadow-lg, 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)));
    --handle-active-shadow: var(--skill-shadow-xl, 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)));
    --handle-hover-scale: 1.1;
    --handle-active-scale: 1.2;
    --handle-transition: var(--skill-duration-fast, 200ms);
    --marks-offset: 12px;
    --mark-size: 2px;
    --mark-bg: var(--skill-gray-400, #BDBDBD);
    --mark-active-bg: var(--skill-primary-500, #0A59F7);
    --labels-offset: 28px;
    --labels-font-size: var(--skill-font-size-caption, 10px);
    --labels-color: var(--skill-gray-500, #8A8A8A);
    --tooltip-offset: 8px;
    --tooltip-bg: var(--skill-gray-900, #1A1A1A);
    --tooltip-color: var(--skill-white, #FFFFFF);
    --tooltip-padding: var(--skill-spacing-xs, 8px);
    --tooltip-radius: var(--skill-radius-base, 4px);
    --tooltip-font-size: var(--skill-font-size-caption, 10px);
    --tooltip-font-weight: var(--skill-font-weight-medium, 500);
    --tooltip-arrow-size: 4px;
    --slider-duration: var(--skill-duration-fast, 200ms);
    --slider-ease: var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }
`

css`
  /* ===== 容器样式 ===== */
  :host {
    display: inline-flex;
    align-items: center;
    justify-content: center;
  }

  .skill-spinner-wrapper {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
  }

  /* ===== Spinner 变体 ===== */

  /* 圆形旋转加载器 */
  .skill-spinner--spinner {
    border-radius: 9999px;
    border-style: solid;
    border-color: currentColor;
    border-top-color: transparent;
    animation: skill-spin 0.8s linear infinite;
  }

  /* xs 尺寸 */
  .skill-spinner--spinner.skill-spinner--xs {
    width: 0.75rem;
    height: 0.75rem;
    border-width: 1px;
  }

  .skill-spinner--spinner.skill-spinner--sm {
    width: 1rem;
    height: 1rem;
    border-width: 1.5px;
  }

  .skill-spinner--spinner.skill-spinner--md {
    width: 1.25rem;
    height: 1.25rem;
    border-width: 2px;
  }

  .skill-spinner--spinner.skill-spinner--lg {
    width: 1.5rem;
    height: 1.5rem;
    border-width: 2px;
  }

  .skill-spinner--spinner.skill-spinner--xl {
    width: 2rem;
    height: 2rem;
    border-width: 3px;
  }

  /* ===== Dots 变体 ===== */
  .skill-spinner--dots {
    display: flex;
    gap: 0.25rem;
  }

  .skill-spinner--dots.skill-spinner--sm {
    gap: 0.25rem;
  }

  .skill-spinner--dots.skill-spinner--md {
    gap: 0.375rem;
  }

  .skill-spinner--dots.skill-spinner--lg {
    gap: 0.5rem;
  }

  .skill-spinner--dots.skill-spinner--xl {
    gap: 0.625rem;
  }

  .skill-spinner__dot {
    border-radius: 9999px;
    background: currentColor;
    animation: skill-pulse 1.4s ease-in-out infinite both;
  }

  .skill-spinner--xs .skill-spinner__dot {
    width: 0.25rem;
    height: 0.25rem;
  }

  .skill-spinner--sm .skill-spinner__dot {
    width: 0.375rem;
    height: 0.375rem;
  }

  .skill-spinner--md .skill-spinner__dot {
    width: 0.5rem;
    height: 0.5rem;
  }

  .skill-spinner--lg .skill-spinner__dot {
    width: 0.625rem;
    height: 0.625rem;
  }

  .skill-spinner--xl .skill-spinner__dot {
    width: 0.75rem;
    height: 0.75rem;
  }

  .skill-spinner__dot:nth-child(1) {
    animation-delay: 0s;
  }

  .skill-spinner__dot:nth-child(2) {
    animation-delay: 0.2s;
  }

  .skill-spinner__dot:nth-child(3) {
    animation-delay: 0.4s;
  }

  /* ===== Pulse 变体 ===== */
  .skill-spinner--pulse {
    border-radius: 9999px;
    background: currentColor;
    animation: skill-ping 1s cubic-bezier(0, 0, 0.2, 1) infinite;
  }

  .skill-spinner--pulse.skill-spinner--xs {
    width: 0.75rem;
    height: 0.75rem;
  }

  .skill-spinner--pulse.skill-spinner--sm {
    width: 1rem;
    height: 1rem;
  }

  .skill-spinner--pulse.skill-spinner--md {
    width: 1.25rem;
    height: 1.25rem;
  }

  .skill-spinner--pulse.skill-spinner--lg {
    width: 1.5rem;
    height: 1.5rem;
  }

  .skill-spinner--pulse.skill-spinner--xl {
    width: 2rem;
    height: 2rem;
  }

  /* ===== Bars 变体 ===== */
  .skill-spinner--bars {
    display: flex;
    align-items: flex-end;
    gap: 0.25rem;
  }

  .skill-spinner--bars.skill-spinner--sm {
    gap: 0.25rem;
  }

  .skill-spinner--bars.skill-spinner--md {
    gap: 0.375rem;
  }

  .skill-spinner--bars.skill-spinner--lg {
    gap: 0.5rem;
  }

  .skill-spinner--bars.skill-spinner--xl {
    gap: 0.625rem;
  }

  .skill-spinner__bar {
    border-radius: 9999px;
    background: currentColor;
    animation: skill-pulse 1.2s ease-in-out infinite both;
  }

  .skill-spinner--xs .skill-spinner__bar {
    width: 0.125rem;
  }

  .skill-spinner--sm .skill-spinner__bar {
    width: 0.25rem;
  }

  .skill-spinner--md .skill-spinner__bar {
    width: 0.25rem;
  }

  .skill-spinner--lg .skill-spinner__bar {
    width: 0.375rem;
  }

  .skill-spinner--xl .skill-spinner__bar {
    width: 0.5rem;
  }

  .skill-spinner__bar:nth-child(1) {
    animation-delay: 0s;
    height: 0.5rem;
  }

  .skill-spinner__bar:nth-child(2) {
    animation-delay: 0.15s;
    height: 0.75rem;
  }

  .skill-spinner__bar:nth-child(3) {
    animation-delay: 0.3s;
    height: 1rem;
  }

  .skill-spinner__bar:nth-child(4) {
    animation-delay: 0.45s;
    height: 0.625rem;
  }

  /* 尺寸调整 */
  .skill-spinner--xs .skill-spinner__bar:nth-child(1) { height: 0.375rem; }
  .skill-spinner--xs .skill-spinner__bar:nth-child(2) { height: 0.5rem; }
  .skill-spinner--xs .skill-spinner__bar:nth-child(3) { height: 0.625rem; }
  .skill-spinner--xs .skill-spinner__bar:nth-child(4) { height: 0.5rem; }

  .skill-spinner--sm .skill-spinner__bar:nth-child(1) { height: 0.5rem; }
  .skill-spinner--sm .skill-spinner__bar:nth-child(2) { height: 0.625rem; }
  .skill-spinner--sm .skill-spinner__bar:nth-child(3) { height: 0.75rem; }
  .skill-spinner--sm .skill-spinner__bar:nth-child(4) { height: 0.625rem; }

  .skill-spinner--lg .skill-spinner__bar:nth-child(1) { height: 0.75rem; }
  .skill-spinner--lg .skill-spinner__bar:nth-child(2) { height: 1rem; }
  .skill-spinner--lg .skill-spinner__bar:nth-child(3) { height: 1.25rem; }
  .skill-spinner--lg .skill-spinner__bar:nth-child(4) { height: 1rem; }

  .skill-spinner--xl .skill-spinner__bar:nth-child(1) { height: 1rem; }
  .skill-spinner--xl .skill-spinner__bar:nth-child(2) { height: 1.25rem; }
  .skill-spinner--xl .skill-spinner__bar:nth-child(3) { height: 1.5rem; }
  .skill-spinner--xl .skill-spinner__bar:nth-child(4) { height: 1.25rem; }

  /* ===== 颜色变体 ===== */
  .skill-spinner--primary {
    color: var(--skill-primary-500, #3b82f6);
  }

  .skill-spinner--secondary {
    color: var(--skill-secondary-500, #64748b);
  }

  .skill-spinner--success {
    color: var(--skill-success-500, #22c55e);
  }

  .skill-spinner--warning {
    color: var(--skill-warning-500, #f59e0b);
  }

  .skill-spinner--error {
    color: var(--skill-error-500, #ef4444);
  }

  .skill-spinner--neutral {
    color: var(--skill-gray-500, #6b7280);
  }

  /* ===== Label 样式 ===== */
  .skill-spinner__label {
    font-size: var(--skill-text-sm, 0.875rem);
    color: var(--skill-gray-600, #4b5563);
  }

  /* ===== 动画 ===== */
  @keyframes skill-spin {
    to {
      transform: rotate(360deg);
    }
  }

  @keyframes skill-pulse {
    0%, 80%, 100% {
      transform: scale(0.8);
      opacity: 0.5;
    }
    40% {
      transform: scale(1);
      opacity: 1;
    }
  }

  @keyframes skill-ping {
    75%, 100% {
      transform: scale(2);
      opacity: 0;
    }
  }
`

css`
  :host {
    display: inline-block;
    font-family: var(--font-sans);
  }

  .switch {
    position: relative;
    display: inline-flex;
    align-items: center;
    flex-shrink: 0;
    cursor: pointer;
    border-radius: 9999px;
    border: 2px solid transparent;
    transition: all var(--duration-200) var(--ease-in-out);
    outline: none;
  }

  .switch:focus {
    box-shadow: 0 0 0 2px var(--color-white), 0 0 0 4px var(--color-gray-500);
  }

  /* Size variants */
  .switch.size-sm {
    width: 32px;
    height: 20px;
  }

  .switch.size-md {
    width: 44px;
    height: 24px;
  }

  .switch.size-lg {
    width: 56px;
    height: 32px;
  }

  /* State colors */
  .switch.checked {
    background-color: var(--color-green-600);
  }

  .switch.checked:focus {
    box-shadow: 0 0 0 2px var(--color-white), 0 0 0 4px var(--color-green-500);
  }

  .switch:not(.checked) {
    background-color: var(--color-gray-200);
  }

  .switch:not(.checked):focus {
    box-shadow: 0 0 0 2px var(--color-white), 0 0 0 4px var(--color-gray-500);
  }

  /* Disabled state */
  .switch.disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  /* Thumb (the sliding button) */
  .thumb {
    pointer-events: none;
    display: inline-block;
    border-radius: 9999px;
    background-color: var(--color-white);
    box-shadow: var(--shadow-lg);
    transition: transform var(--duration-200) var(--ease-in-out);
    transform: translateX(0);
  }

  /* Thumb sizes */
  .switch.size-sm .thumb {
    width: 12px;
    height: 12px;
    margin: 2px;
  }

  .switch.size-md .thumb {
    width: 16px;
    height: 16px;
    margin: 2px;
  }

  .switch.size-lg .thumb {
    width: 20px;
    height: 20px;
    margin: 4px;
  }

  /* Checked state - slide thumb to the right */
  .switch.size-sm.checked .thumb {
    transform: translateX(12px);
  }

  .switch.size-md.checked .thumb {
    transform: translateX(20px);
  }

  .switch.size-lg.checked .thumb {
    transform: translateX(24px);
  }
`

css`
  :host {
    display: inline-flex;
    align-items: center;
    position: relative;
    vertical-align: middle;
  }

  .skill-label {
    display: inline-flex;
    align-items: center;
    gap: var(--label-spacing, var(--skill-spacing-xs, 8px));
    padding: var(--label-padding-y, var(--skill-spacing-xs, 8px)) var(--label-padding-x, var(--skill-spacing-sm, 12px));
    background: var(--label-bg, var(--skill-gray-100, #F1F3F5));
    color: var(--label-color, var(--skill-gray-700, #5A5A5A));
    border: var(--label-border-width, 1px) solid var(--label-border-color, var(--skill-gray-300, #D1D5DB));
    border-radius: var(--label-radius, var(--skill-radius-full, 9999px));
    font-size: var(--label-font-size, var(--skill-font-size-body-3, 12px));
    font-weight: var(--label-font-weight, var(--skill-font-weight-medium, 500));
    line-height: var(--label-line-height, 1.2);
    white-space: nowrap;
    text-align: center;
    transition: all var(--label-duration, var(--skill-duration-fast, 200ms)) var(--label-ease, var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1)));
    box-sizing: border-box;
    cursor: default;
    outline: none;
    position: relative;
    overflow: hidden;
    max-width: var(--label-max-width, none);
    min-width: var(--label-min-width, auto);
    min-height: var(--label-min-height, auto);
  }

  .skill-label:focus-visible {
    outline: 2px solid var(--skill-primary-500, #0A59F7);
    outline-offset: 2px;
  }

  .skill-label__content {
    display: inline-block;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    min-width: 0;
    flex: 1;
  }

  /* Mode-specific styles */
  :host([mode='badge']) .skill-label {
    justify-content: center;
    min-width: var(--label-min-width, 20px);
    min-height: var(--label-min-height, 20px);
  }

  :host([mode='tag']) .skill-label {
    cursor: default;
    user-select: none;
  }

  :host([mode='chip']) .skill-label {
    user-select: none;
    padding: var(--label-padding-y, var(--skill-spacing-xs, 8px)) var(--label-padding-x, var(--skill-spacing-sm, 12px));
  }

  /* Size variants */
  :host([size='xs']) .skill-label {
    --label-padding-y: var(--skill-spacing-2xs, 4px);
    --label-padding-x: var(--skill-spacing-xs, 8px);
    --label-font-size: 8px;
    --label-spacing: var(--skill-spacing-2xs, 4px);
    --label-min-width: 16px;
    --label-min-height: 16px;
  }

  :host([size='sm']) .skill-label {
    --label-padding-y: var(--skill-spacing-2xs, 4px);
    --label-padding-x: var(--skill-spacing-xs, 8px);
    --label-font-size: var(--skill-font-size-caption, 10px);
    --label-spacing: var(--skill-spacing-2xs, 4px);
    --label-min-width: 18px;
    --label-min-height: 18px;
  }

  :host([size='md']) .skill-label {
    --label-padding-y: var(--skill-spacing-xs, 8px);
    --label-padding-x: var(--skill-spacing-sm, 12px);
    --label-font-size: var(--skill-font-size-body-3, 12px);
    --label-spacing: var(--skill-spacing-2xs, 4px);
    --label-min-width: 24px;
    --label-min-height: 24px;
  }

  :host([size='lg']) .skill-label {
    --label-padding-y: var(--skill-spacing-sm, 12px);
    --label-padding-x: var(--skill-spacing-md, 16px);
    --label-font-size: var(--skill-font-size-body-2, 14px);
    --label-spacing: var(--skill-spacing-xs, 8px);
    --label-min-width: 32px;
    --label-min-height: 32px;
  }

  :host([size='xl']) .skill-label {
    --label-padding-y: var(--skill-spacing-md, 16px);
    --label-padding-x: var(--skill-spacing-lg, 20px);
    --label-font-size: var(--skill-font-size-body-1, 16px);
    --label-spacing: var(--skill-spacing-sm, 12px);
    --label-min-width: 40px;
    --label-min-height: 40px;
  }

  /* Shape variants */
  :host([shape='square']) .skill-label {
    --label-radius: var(--skill-radius-xs, 2px);
  }

  :host([shape='rounded']) .skill-label {
    --label-radius: var(--skill-radius-sm, 4px);
  }

  :host([shape='pill']) .skill-label {
    --label-radius: var(--skill-radius-full, 9999px);
  }

  :host([shape='circle']) .skill-label {
    --label-radius: var(--skill-radius-full, 9999px);
    width: var(--label-size, var(--label-min-width, 20px));
    height: var(--label-size, var(--label-min-height, 20px));
    padding: 0;
  }

  :host([shape='circle'][size='xs']) .skill-label {
    --label-size: 16px;
  }

  :host([shape='circle'][size='sm']) .skill-label {
    --label-size: 18px;
  }

  :host([shape='circle'][size='md']) .skill-label {
    --label-size: 24px;
  }

  :host([shape='circle'][size='lg']) .skill-label {
    --label-size: 32px;
  }

  :host([shape='circle'][size='xl']) .skill-label {
    --label-size: 40px;
  }

  :host([shape='dot']) .skill-label {
    width: var(--dot-size, 8px);
    height: var(--dot-size, 8px);
    min-width: var(--dot-size, 8px);
    min-height: var(--dot-size, 8px);
    padding: 0;
    border-radius: var(--skill-radius-full, 9999px);
  }

  :host([shape='dot'][size='xs']) .skill-label {
    --dot-size: 6px;
  }

  :host([shape='dot'][size='sm']) .skill-label {
    --dot-size: 8px;
  }

  :host([shape='dot'][size='md']) .skill-label {
    --dot-size: 10px;
  }

  :host([shape='dot'][size='lg']) .skill-label {
    --dot-size: 12px;
  }

  :host([shape='dot'][size='xl']) .skill-label {
    --dot-size: 14px;
  }

  /* Color variants - Default (Badge mode uses solid colors, Tag/Chip use light backgrounds) */
  :host([color='primary']) .skill-label {
    --label-bg: var(--skill-primary-500, #0A59F7);
    --label-color: var(--skill-white, #FFFFFF);
    --label-border-color: var(--skill-primary-500, #0A59F7);
  }

  :host([color='primary'][mode='tag']) .skill-label,
  :host([color='primary'][mode='chip']) .skill-label {
    --label-bg: var(--skill-primary-50, #EFF6FF);
    --label-color: var(--skill-primary-700, #1D4ED8);
    --label-border-color: var(--skill-primary-200, #BFDBFE);
  }

  :host([color='secondary']) .skill-label {
    --label-bg: var(--skill-secondary-500, #00D4AA);
    --label-color: var(--skill-white, #FFFFFF);
    --label-border-color: var(--skill-secondary-500, #00D4AA);
  }

  :host([color='secondary'][mode='tag']) .skill-label,
  :host([color='secondary'][mode='chip']) .skill-label {
    --label-bg: var(--skill-secondary-50, #F0FDFA);
    --label-color: var(--skill-secondary-700, #0F766E);
    --label-border-color: var(--skill-secondary-200, #A7F3D0);
  }

  :host([color='success']) .skill-label {
    --label-bg: var(--skill-success-500, #00D4AA);
    --label-color: var(--skill-white, #FFFFFF);
    --label-border-color: var(--skill-success-500, #00D4AA);
  }

  :host([color='success'][mode='tag']) .skill-label,
  :host([color='success'][mode='chip']) .skill-label {
    --label-bg: var(--skill-success-50, #F0FDF4);
    --label-color: var(--skill-success-700, #15803D);
    --label-border-color: var(--skill-success-200, #BBF7D0);
  }

  :host([color='warning']) .skill-label {
    --label-bg: var(--skill-warning-500, #FFB400);
    --label-color: var(--skill-white, #FFFFFF);
    --label-border-color: var(--skill-warning-500, #FFB400);
  }

  :host([color='warning'][mode='tag']) .skill-label,
  :host([color='warning'][mode='chip']) .skill-label {
    --label-bg: var(--skill-warning-50, #FFFBEB);
    --label-color: var(--skill-warning-700, #A16207);
    --label-border-color: var(--skill-warning-200, #FED7AA);
  }

  :host([color='error']) .skill-label {
    --label-bg: var(--skill-error-500, #FA2A2D);
    --label-color: var(--skill-white, #FFFFFF);
    --label-border-color: var(--skill-error-500, #FA2A2D);
  }

  :host([color='error'][mode='tag']) .skill-label,
  :host([color='error'][mode='chip']) .skill-label {
    --label-bg: var(--skill-error-50, #FEF2F2);
    --label-color: var(--skill-error-700, #B91C1C);
    --label-border-color: var(--skill-error-200, #FECACA);
  }

  :host([color='info']) .skill-label {
    --label-bg: var(--skill-info-500, #0A59F7);
    --label-color: var(--skill-white, #FFFFFF);
    --label-border-color: var(--skill-info-500, #0A59F7);
  }

  :host([color='info'][mode='tag']) .skill-label,
  :host([color='info'][mode='chip']) .skill-label {
    --label-bg: var(--skill-info-50, #EFF6FF);
    --label-color: var(--skill-info-700, #0369A1);
    --label-border-color: var(--skill-info-200, #BAE6FD);
  }

  :host([color='gray']) .skill-label {
    --label-bg: var(--skill-gray-500, #8A8A8A);
    --label-color: var(--skill-white, #FFFFFF);
    --label-border-color: var(--skill-gray-500, #8A8A8A);
  }

  :host([color='gray'][mode='tag']) .skill-label,
  :host([color='gray'][mode='chip']) .skill-label {
    --label-bg: var(--skill-gray-50, #F9FAFB);
    --label-color: var(--skill-gray-700, #374151);
    --label-border-color: var(--skill-gray-200, #E5E7EB);
  }

  /* Variant styles */
  :host([variant='light']) .skill-label {
    --label-bg: var(--skill-gray-100, #F1F3F5);
    --label-color: var(--skill-gray-700, #5A5A5A);
    --label-border-color: var(--skill-gray-200, #E5E7EB);
  }

  :host([variant='light'][color='primary']) .skill-label {
    --label-bg: var(--skill-primary-100, #E6F0FF);
    --label-color: var(--skill-primary-700, #0036A0);
  }

  :host([variant='light'][color='secondary']) .skill-label {
    --label-bg: var(--skill-secondary-100, #E6FBF5);
    --label-color: var(--skill-secondary-700, #006B5D);
  }

  :host([variant='light'][color='success']) .skill-label {
    --label-bg: var(--skill-success-100, #E6FBF5);
    --label-color: var(--skill-success-700, #006B5D);
  }

  :host([variant='light'][color='warning']) .skill-label {
    --label-bg: var(--skill-warning-100, #FFF8E6);
    --label-color: var(--skill-warning-700, #8B5A00);
  }

  :host([variant='light'][color='error']) .skill-label {
    --label-bg: var(--skill-error-100, #FEE6E7);
    --label-color: var(--skill-error-700, #8B1E1F);
  }

  :host([variant='light'][color='info']) .skill-label {
    --label-bg: var(--skill-info-100, #E6F0FF);
    --label-color: var(--skill-info-700, #0036A0);
  }

  :host([variant='outline']) .skill-label {
    --label-bg: transparent;
    --label-border-width: 1.5px;
  }

  :host([variant='outline'][color='primary']) .skill-label {
    --label-color: var(--skill-primary-600, #2563EB);
    --label-border-color: var(--skill-primary-600, #2563EB);
  }

  :host([variant='outline'][color='secondary']) .skill-label {
    --label-color: var(--skill-secondary-600, #0D9488);
    --label-border-color: var(--skill-secondary-600, #0D9488);
  }

  :host([variant='outline'][color='success']) .skill-label {
    --label-color: var(--skill-success-600, #16A34A);
    --label-border-color: var(--skill-success-600, #16A34A);
  }

  :host([variant='outline'][color='warning']) .skill-label {
    --label-color: var(--skill-warning-600, #CA8A04);
    --label-border-color: var(--skill-warning-600, #CA8A04);
  }

  :host([variant='outline'][color='error']) .skill-label {
    --label-color: var(--skill-error-600, #DC2626);
    --label-border-color: var(--skill-error-600, #DC2626);
  }

  :host([variant='outline'][color='info']) .skill-label {
    --label-color: var(--skill-info-600, #0284C7);
    --label-border-color: var(--skill-info-600, #0284C7);
  }

  :host([variant='text']) .skill-label {
    --label-bg: transparent;
    --label-border-width: 0;
    --label-padding-y: 0;
    --label-padding-x: 0;
    --label-radius: 0;
  }

  :host([variant='text'][color='primary']) .skill-label {
    --label-color: var(--skill-primary-600, #2563EB);
  }

  :host([variant='text'][color='secondary']) .skill-label {
    --label-color: var(--skill-secondary-600, #0D9488);
  }

  :host([variant='text'][color='success']) .skill-label {
    --label-color: var(--skill-success-600, #16A34A);
  }

  :host([variant='text'][color='warning']) .skill-label {
    --label-color: var(--skill-warning-600, #CA8A04);
  }

  :host([variant='text'][color='error']) .skill-label {
    --label-color: var(--skill-error-600, #DC2626);
  }

  :host([variant='text'][color='info']) .skill-label {
    --label-color: var(--skill-info-600, #0284C7);
  }

  :host([variant='filled']) .skill-label {
    --label-border-width: 0;
  }

  :host([variant='filled'][color='primary']) .skill-label {
    --label-bg: var(--skill-primary-500, #0A59F7);
    --label-color: var(--skill-white, #FFFFFF);
  }

  :host([variant='filled'][color='secondary']) .skill-label {
    --label-bg: var(--skill-secondary-500, #00D4AA);
    --label-color: var(--skill-white, #FFFFFF);
  }

  :host([variant='filled'][color='success']) .skill-label {
    --label-bg: var(--skill-success-500, #00D4AA);
    --label-color: var(--skill-white, #FFFFFF);
  }

  :host([variant='filled'][color='warning']) .skill-label {
    --label-bg: var(--skill-warning-500, #FFB400);
    --label-color: var(--skill-white, #FFFFFF);
  }

  :host([variant='filled'][color='error']) .skill-label {
    --label-bg: var(--skill-error-500, #FA2A2D);
    --label-color: var(--skill-white, #FFFFFF);
  }

  :host([variant='filled'][color='info']) .skill-label {
    --label-bg: var(--skill-info-500, #0A59F7);
    --label-color: var(--skill-white, #FFFFFF);
  }

  :host([variant='soft']) .skill-label {
    --label-border-width: 0;
    opacity: 0.8;
  }

  /* Interactive states */
  :host([clickable]) .skill-label {
    cursor: pointer;
  }

  :host([clickable]) .skill-label:hover {
    transform: translateY(-1px);
    box-shadow: var(--skill-shadow-sm, 0 1px 2px rgba(0, 0, 0, 0.05));
  }

  :host([clickable]) .skill-label:active {
    transform: translateY(0);
    box-shadow: none;
  }

  :host([clickable][mode='tag']) .skill-label:hover {
    transform: none;
  }

  :host([clickable][mode='tag']) .skill-label:hover {
    background: var(--label-hover-bg, var(--skill-gray-50, #F9FAFB));
    border-color: var(--label-hover-border-color, var(--skill-gray-300, #D1D5DB));
  }

  :host([clickable][mode='tag'][color='primary']) .skill-label:hover {
    background: var(--skill-primary-100, #DBEAFE);
    border-color: var(--skill-primary-300, #93C5FD);
  }

  :host([clickable][mode='tag'][color='secondary']) .skill-label:hover {
    background: var(--skill-secondary-100, #CCFBF1);
    border-color: var(--skill-secondary-300, #6EE7B7);
  }

  :host([clickable][mode='tag'][color='success']) .skill-label:hover {
    background: var(--skill-success-100, #DCFCE7);
    border-color: var(--skill-success-300, #86EFAC);
  }

  :host([clickable][mode='tag'][color='warning']) .skill-label:hover {
    background: var(--skill-warning-100, #FEF3C7);
    border-color: var(--skill-warning-300, #FCD34D);
  }

  :host([clickable][mode='tag'][color='error']) .skill-label:hover {
    background: var(--skill-error-100, #FEE2E2);
    border-color: var(--skill-error-300, #FCA5A5);
  }

  :host([clickable][mode='tag'][color='info']) .skill-label:hover {
    background: var(--skill-info-100, #DBEAFE);
    border-color: var(--skill-info-300, #7DD3FC);
  }

  /* Active/Selected states */
  :host([active]) .skill-label,
  :host([selected]) .skill-label {
    --label-bg: var(--skill-primary-500, #0A59F7);
    --label-color: var(--skill-white, #FFFFFF);
    --label-border-color: var(--skill-primary-500, #0A59F7);
    box-shadow: var(--skill-shadow-sm, 0 1px 2px rgba(0, 0, 0, 0.05));
  }

  :host([active][color='secondary']) .skill-label,
  :host([selected][color='secondary']) .skill-label {
    --label-bg: var(--skill-secondary-500, #00D4AA);
    --label-border-color: var(--skill-secondary-500, #00D4AA);
  }

  :host([active][color='success']) .skill-label,
  :host([selected][color='success']) .skill-label {
    --label-bg: var(--skill-success-500, #00D4AA);
    --label-border-color: var(--skill-success-500, #00D4AA);
  }

  :host([active][color='warning']) .skill-label,
  :host([selected][color='warning']) .skill-label {
    --label-bg: var(--skill-warning-500, #FFB400);
    --label-border-color: var(--skill-warning-500, #FFB400);
  }

  :host([active][color='error']) .skill-label,
  :host([selected][color='error']) .skill-label {
    --label-bg: var(--skill-error-500, #FA2A2D);
    --label-border-color: var(--skill-error-500, #FA2A2D);
  }

  :host([active][color='info']) .skill-label,
  :host([selected][color='info']) .skill-label {
    --label-bg: var(--skill-info-500, #0A59F7);
    --label-border-color: var(--skill-info-500, #0A59F7);
  }

  /* Disabled state */
  :host([disabled]) .skill-label {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
  }

  /* Counter font (Badge mode) */
  :host([counter]) .skill-label {
    font-family: var(--skill-font-family-mono, 'JetBrains Mono', 'SF Mono', Consolas, monospace);
    font-variant-numeric: tabular-nums;
  }

  /* Pulse animation (Badge mode) */
  :host([pulse]) .skill-label {
    position: relative;
  }

  :host([pulse]) .skill-label::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border-radius: inherit;
    background: inherit;
    animation: label-pulse 2s infinite;
    z-index: -1;
  }

  @keyframes label-pulse {
    0% {
      opacity: 1;
      transform: scale(1);
    }
    50% {
      opacity: 0.3;
      transform: scale(1.3);
    }
    100% {
      opacity: 0;
      transform: scale(1.5);
    }
  }

  /* Close button (Tag mode) */
  .skill-label__close {
    display: flex;
    align-items: center;
    justify-content: center;
    border: none;
    background: none;
    padding: 0;
    margin: 0 calc(var(--skill-spacing-2xs, 4px) * -1);
    color: var(--label-close-color, currentColor);
    opacity: 0.6;
    cursor: pointer;
    border-radius: var(--skill-radius-full, 9999px);
    transition: all var(--skill-duration-fast, 200ms);
    font-size: 1em;
    width: 1.2em;
    height: 1.2em;
    flex-shrink: 0;
  }

  .skill-label__close:hover {
    opacity: 1;
    background: var(--label-close-hover-bg, rgba(0, 0, 0, 0.1));
  }

  .skill-label__close:focus-visible {
    outline: 2px solid var(--skill-primary-500, #0A59F7);
    outline-offset: 1px;
    opacity: 1;
  }

  /* Remove button (Chip mode) */
  .skill-label__remove {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    width: var(--remove-size, 16px);
    height: var(--remove-size, 16px);
    border-radius: var(--remove-radius, var(--skill-radius-full, 9999px));
    background: var(--remove-bg, rgba(0, 0, 0, 0.1));
    color: var(--remove-color, currentColor);
    border: none;
    cursor: pointer;
    transition: all var(--skill-duration-fast, 200ms) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
    font-size: 10px;
    line-height: 1;
    flex-shrink: 0;
  }

  .skill-label__remove:hover {
    --remove-bg: rgba(0, 0, 0, 0.2);
    transform: scale(1.1);
  }

  .skill-label__remove:active {
    transform: scale(0.95);
  }

  :host([color='primary']) .skill-label__remove {
    --remove-bg: rgba(255, 255, 255, 0.2);
    --remove-color: var(--skill-white, #FFFFFF);
  }

  :host([color='primary'][variant='outline']) .skill-label__remove {
    --remove-bg: var(--skill-primary-100, #E6F0FF);
    --remove-color: var(--skill-primary-600, #0047D3);
  }

  :host([selected]) .skill-label__remove {
    --remove-bg: rgba(255, 255, 255, 0.2);
    --remove-color: var(--skill-white, #FFFFFF);
  }

  /* Size-specific remove button */
  :host([size='xs']) .skill-label__remove,
  :host([size='xs']) .skill-label__close {
    --remove-size: 12px;
    font-size: 8px;
  }

  :host([size='sm']) .skill-label__remove,
  :host([size='sm']) .skill-label__close {
    --remove-size: 14px;
    font-size: 9px;
  }

  :host([size='lg']) .skill-label__remove,
  :host([size='lg']) .skill-label__close {
    --remove-size: 18px;
    font-size: 11px;
  }

  :host([size='xl']) .skill-label__remove,
  :host([size='xl']) .skill-label__close {
    --remove-size: 20px;
    font-size: 12px;
  }

  /* Avatar (Chip mode) */
  .skill-label__avatar {
    width: var(--avatar-size, 20px);
    height: var(--avatar-size, 20px);
    border-radius: var(--avatar-radius, var(--skill-radius-full, 9999px));
    object-fit: cover;
    flex-shrink: 0;
  }

  :host([size='xs']) .skill-label__avatar {
    --avatar-size: 16px;
  }

  :host([size='sm']) .skill-label__avatar {
    --avatar-size: 18px;
  }

  :host([size='lg']) .skill-label__avatar {
    --avatar-size: 24px;
  }

  :host([size='xl']) .skill-label__avatar {
    --avatar-size: 28px;
  }

  /* Prefix and suffix */
  .skill-label__prefix,
  .skill-label__suffix {
    display: inline-flex;
    align-items: center;
    flex-shrink: 0;
  }

  /* Badge (Chip mode) */
  .skill-label__badge {
    position: absolute;
    top: -4px;
    right: -4px;
    min-width: 16px;
    height: 16px;
    padding: 0 4px;
    background: var(--skill-error-500, #FA2A2D);
    color: var(--skill-white, #FFFFFF);
    border-radius: var(--skill-radius-full, 9999px);
    font-size: 10px;
    font-weight: var(--skill-font-weight-medium, 500);
    line-height: 16px;
    text-align: center;
    z-index: 1;
  }

  /* Ripple effect (Chip mode) */
  :host([clickable][ripple]) .skill-label {
    position: relative;
    overflow: hidden;
  }

  :host([clickable][ripple]) .skill-label::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.3);
    transform: translate(-50%, -50%);
    transition: width 0.6s, height 0.6s;
  }

  :host([clickable][ripple]) .skill-label.ripple::after {
    width: 200px;
    height: 200px;
  }

  /* Positioned badges (Badge mode) */
  :host([positioned]) {
    position: absolute;
    z-index: var(--label-z-index, 10);
  }

  :host([positioned][position='top-right']) {
    top: var(--label-offset-y, -8px);
    right: var(--label-offset-x, -8px);
  }

  :host([positioned][position='top-left']) {
    top: var(--label-offset-y, -8px);
    left: var(--label-offset-x, -8px);
  }

  :host([positioned][position='bottom-right']) {
    bottom: var(--label-offset-y, -8px);
    right: var(--label-offset-x, -8px);
  }

  :host([positioned][position='bottom-left']) {
    bottom: var(--label-offset-y, -8px);
    left: var(--label-offset-x, -8px);
  }

  :host([positioned][position='top-center']) {
    top: var(--label-offset-y, -8px);
    left: 50%;
    transform: translateX(-50%);
  }

  :host([positioned][position='bottom-center']) {
    bottom: var(--label-offset-y, -8px);
    left: 50%;
    transform: translateX(-50%);
  }

  /* Responsive design */
  @media (max-width: 768px) {
    :host([responsive]) .skill-label {
      --label-font-size: var(--skill-font-size-caption, 10px);
      --label-padding-y: var(--skill-spacing-2xs, 4px);
      --label-padding-x: var(--skill-spacing-xs, 8px);
    }
  }

  /* High contrast mode support */
  @media (prefers-contrast: high) {
    .skill-label {
      --label-border-width: 1px;
      font-weight: var(--skill-font-weight-semibold, 600);
    }

    .skill-label__close:focus-visible {
      outline-width: 3px;
    }
  }

  /* Reduced motion support */
  @media (prefers-reduced-motion: reduce) {
    .skill-label,
    .skill-label__close,
    .skill-label__remove,
    :host([clickable]) .skill-label {
      transition: none;
    }

    :host([clickable]) .skill-label:hover,
    .skill-label__close:hover,
    .skill-label__remove:hover {
      transform: none;
    }

    :host([pulse]) .skill-label::after,
    :host([clickable][ripple]) .skill-label::after {
      animation: none;
    }
  }

  /* Custom CSS variables defaults */
  .skill-label {
    --label-bg: var(--skill-gray-100, #F1F3F5);
    --label-color: var(--skill-gray-700, #5A5A5A);
    --label-border-color: var(--skill-gray-300, #D1D5DB);
    --label-border-width: 1px;
    --label-radius: var(--skill-radius-full, 9999px);
    --label-font-size: var(--skill-font-size-body-3, 12px);
    --label-font-weight: var(--skill-font-weight-medium, 500);
    --label-line-height: 1.2;
    --label-duration: var(--skill-duration-fast, 200ms);
    --label-ease: var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
    --label-spacing: var(--skill-spacing-xs, 8px);
    --label-padding-y: var(--skill-spacing-xs, 8px);
    --label-padding-x: var(--skill-spacing-sm, 12px);
    --label-min-width: auto;
    --label-min-height: auto;
    --label-max-width: none;
    --remove-size: 16px;
    --remove-radius: var(--skill-radius-full, 9999px);
    --remove-bg: rgba(0, 0, 0, 0.1);
    --remove-color: currentColor;
    --avatar-size: 20px;
    --avatar-radius: var(--skill-radius-full, 9999px);
    --dot-size: 8px;
    --label-offset-x: -8px;
    --label-offset-y: -8px;
    --label-z-index: 10;
  }
`

css`
  :host {
    display: block;
    font-family: var(--font-sans);
  }

  .textarea-wrapper {
    display: flex;
    flex-direction: column;
    gap: var(--spacing-1-5);
  }

  .textarea-label {
    display: block;
    font-size: var(--text-sm);
    font-weight: 500;
    color: var(--color-gray-700);
    margin-bottom: var(--spacing-1);
  }

  .textarea-container {
    position: relative;
    width: 100%;
  }

  textarea {
    display: block;
    width: 100%;
    padding: var(--spacing-3) var(--spacing-4);
    border: 1px solid var(--color-gray-200);
    border-radius: 0;
    background-color: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(4px);
    font-family: var(--font-sans);
    font-size: var(--text-sm);
    font-weight: 400;
    line-height: 1.625;
    color: var(--color-gray-900);
    transition: all var(--duration-200) var(--ease-in-out);
    min-height: 80px;
  }

  textarea::placeholder {
    color: var(--color-gray-500);
  }

  textarea:hover:not(:disabled) {
    background-color: rgba(255, 255, 255, 0.9);
    border-color: var(--color-gray-300);
  }

  textarea:focus {
    outline: none;
    border-color: var(--color-primary-500);
    background-color: rgba(255, 255, 255, 1);
  }

  textarea:disabled {
    background-color: var(--color-gray-50);
    color: var(--color-gray-500);
    cursor: not-allowed;
    opacity: 0.6;
  }

  /* Variant: filled */
  .textarea-container.variant-filled textarea {
    background-color: var(--color-gray-100);
    border-color: transparent;
  }

  .textarea-container.variant-filled textarea:hover:not(:disabled) {
    background-color: var(--color-gray-200);
  }

  .textarea-container.variant-filled textarea:focus {
    background-color: var(--color-white);
    border-color: var(--color-primary-500);
  }

  /* Error state */
  .textarea-container.has-error textarea {
    border-color: var(--color-red-300);
    color: var(--color-red-900);
  }

  .textarea-container.has-error textarea:focus {
    border-color: var(--color-red-500);
  }

  /* Resize variants */
  textarea.resize-none {
    resize: none;
  }

  textarea.resize-both {
    resize: both;
  }

  textarea.resize-horizontal {
    resize: horizontal;
  }

  textarea.resize-vertical {
    resize: vertical;
  }

  .helper-text {
    font-size: var(--text-sm);
    color: var(--color-gray-500);
    margin-top: var(--spacing-1);
  }

  .error-text {
    font-size: var(--text-sm);
    color: var(--color-red-600);
    margin-top: var(--spacing-1);
  }
`

css`
  :host {
    display: block;
  }

  .skill-notification {
    display: flex;
    align-items: flex-start;
    gap: var(--notification-gap, var(--skill-spacing-sm, 12px));
    padding: var(--notification-padding, var(--skill-spacing-md, 16px));
    border-radius: var(--notification-radius, var(--skill-radius-md, 6px));
    transition: all var(--notification-duration, var(--skill-duration-fast, 200ms)) var(--notification-ease, var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1)));
    position: relative;
    overflow: hidden;
    box-sizing: border-box;
  }

  /* Size variants */
  .skill-notification--sm {
    --notification-padding: var(--skill-spacing-sm, 12px);
    --notification-gap: var(--skill-spacing-xs, 8px);
    font-size: var(--skill-font-size-caption, 10px);
  }

  .skill-notification--md {
    --notification-padding: var(--skill-spacing-md, 16px);
    --notification-gap: var(--skill-spacing-sm, 12px);
    font-size: var(--skill-font-size-body-3, 12px);
  }

  .skill-notification--lg {
    --notification-padding: var(--skill-spacing-lg, 20px);
    --notification-gap: var(--skill-spacing-md, 16px);
    font-size: var(--skill-font-size-body-2, 14px);
  }

  /* Type colors */
  .skill-notification--success {
    --notification-bg: var(--skill-success-50, #f0fdf4);
    --notification-color: var(--skill-success-800, #166534);
    --notification-border: var(--skill-success-200, #bbf7d0);
    --notification-icon-color: var(--skill-success-600, #16a34a);
  }

  .skill-notification--error {
    --notification-bg: var(--skill-error-50, #fef2f2);
    --notification-color: var(--skill-error-800, #991b1b);
    --notification-border: var(--skill-error-200, #fecaca);
    --notification-icon-color: var(--skill-error-600, #dc2626);
  }

  .skill-notification--warning {
    --notification-bg: var(--skill-warning-50, #fffbeb);
    --notification-color: var(--skill-warning-800, #92400e);
    --notification-border: var(--skill-warning-200, #fed7aa);
    --notification-icon-color: var(--skill-warning-600, #d97706);
  }

  .skill-notification--info {
    --notification-bg: var(--skill-info-50, #eff6ff);
    --notification-color: var(--skill-info-800, #1e40af);
    --notification-border: var(--skill-info-200, #bfdbfe);
    --notification-icon-color: var(--skill-info-600, #2563eb);
  }

  .skill-notification--default,
  .skill-notification--neutral {
    --notification-bg: var(--skill-gray-50, #f9fafb);
    --notification-color: var(--skill-gray-800, #1f2937);
    --notification-border: var(--skill-gray-200, #e5e7eb);
    --notification-icon-color: var(--skill-gray-600, #4b5563);
  }

  /* Variant styles */
  .skill-notification--alert {
    background: var(--notification-bg);
    color: var(--notification-color);
    border: 1px solid var(--notification-border);
  }

  .skill-notification--toast {
    background: var(--notification-bg);
    color: var(--notification-color);
    border: 1px solid var(--notification-border);
    box-shadow: var(--skill-shadow-lg, 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05));
    position: fixed;
    z-index: var(--notification-z-index, 1000);
    min-width: 300px;
    max-width: 500px;
  }

  .skill-notification--bar {
    background: var(--notification-bg);
    color: var(--notification-color);
    border: 1px solid var(--notification-border);
    width: 100%;
    border-radius: 0;
  }

  .skill-notification--bar::before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    bottom: 0;
    width: 4px;
    background: var(--notification-icon-color);
  }

  /* Icon styles */
  .skill-notification__icon {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
    width: 20px;
    height: 20px;
    color: var(--notification-icon-color);
  }

  .skill-notification--sm .skill-notification__icon {
    width: 16px;
    height: 16px;
  }

  .skill-notification--lg .skill-notification__icon {
    width: 24px;
    height: 24px;
  }

  /* Content styles */
  .skill-notification__content {
    flex: 1;
    min-width: 0;
  }

  .skill-notification__title {
    font-weight: var(--notification-title-weight, var(--skill-font-weight-semibold, 600));
    margin-bottom: var(--skill-spacing-xs, 4px);
    line-height: 1.4;
  }

  .skill-notification__message {
    line-height: 1.5;
    color: var(--notification-message-color, var(--notification-color));
  }

  .skill-notification__message--expandable {
    position: relative;
  }

  .skill-notification__expand-toggle {
    background: none;
    border: none;
    color: var(--notification-icon-color);
    cursor: pointer;
    font-size: inherit;
    padding: 0;
    margin-top: var(--skill-spacing-xs, 4px);
    text-decoration: underline;
  }

  .skill-notification__expand-toggle:hover {
    opacity: 0.8;
  }

  /* Actions styles */
  .skill-notification__actions {
    display: flex;
    gap: var(--skill-spacing-xs, 8px);
    align-items: center;
    flex-shrink: 0;
  }

  /* Close button styles */
  .skill-notification__close {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 20px;
    height: 20px;
    border: none;
    background: none;
    color: var(--notification-close-color, var(--notification-color));
    cursor: pointer;
    border-radius: var(--skill-radius-full, 9999px);
    opacity: 0.6;
    transition: all var(--skill-duration-fast, 200ms);
    flex-shrink: 0;
  }

  .skill-notification__close:hover {
    opacity: 1;
    background: rgba(0, 0, 0, 0.05);
  }

  .skill-notification__close:focus {
    outline: 2px solid var(--notification-icon-color);
    outline-offset: 2px;
  }

  .skill-notification--sm .skill-notification__close {
    width: 16px;
    height: 16px;
  }

  .skill-notification--lg .skill-notification__close {
    width: 24px;
    height: 24px;
  }

  /* Progress bar styles */
  .skill-notification__progress {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 3px;
    background: var(--skill-gray-200, #e5e7eb);
    overflow: hidden;
  }

  .skill-notification__progress-bar {
    height: 100%;
    background: var(--notification-icon-color);
    transition: width linear;
  }

  /* Positioning for toast variant */
  .skill-notification--toast[position="top-left"] {
    top: 20px;
    left: 20px;
    right: auto;
    bottom: auto;
  }

  .skill-notification--toast[position="top-right"] {
    top: 20px;
    right: 20px;
    left: auto;
    bottom: auto;
  }

  .skill-notification--toast[position="top-center"] {
    top: 20px;
    left: 50%;
    transform: translateX(-50%);
    right: auto;
    bottom: auto;
  }

  .skill-notification--toast[position="bottom-left"] {
    bottom: 20px;
    left: 20px;
    right: auto;
    top: auto;
  }

  .skill-notification--toast[position="bottom-right"] {
    bottom: 20px;
    right: 20px;
    left: auto;
    top: auto;
  }

  .skill-notification--toast[position="bottom-center"] {
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    right: auto;
    top: auto;
  }

  .skill-notification--toast[position="center"] {
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    right: auto;
    bottom: auto;
  }

  /* Interactive states */
  .skill-notification[clickable] {
    cursor: pointer;
  }

  .skill-notification[clickable]:hover {
    transform: translateY(-1px);
    box-shadow: var(--skill-shadow-xl, 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04));
  }

  /* Animations */
  @keyframes notificationSlideIn {
    from {
      opacity: 0;
      transform: translateY(-20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes notificationSlideOut {
    from {
      opacity: 1;
      transform: translateY(0);
    }
    to {
      opacity: 0;
      transform: translateY(-20px);
    }
  }

  .skill-notification {
    animation: notificationSlideIn 0.3s ease-out;
  }

  .skill-notification[visible="false"] {
    animation: notificationSlideOut 0.3s ease-out forwards;
  }

  /* Responsive design */
  @media (max-width: 768px) {
    .skill-notification--toast {
      min-width: auto;
      left: 16px !important;
      right: 16px !important;
      transform: none !important;
      width: calc(100% - 32px);
    }

    .skill-notification--toast[position="top-center"],
    .skill-notification--toast[position="bottom-center"],
    .skill-notification--toast[position="center"] {
      transform: none !important;
      left: 16px;
    }
  }

  /* High contrast mode support */
  @media (prefers-contrast: high) {
    .skill-notification {
      border-width: 2px;
    }
  }

  /* Reduced motion support */
  @media (prefers-reduced-motion: reduce) {
    .skill-notification,
    .skill-notification__progress-bar {
      transition: none;
      animation: none;
    }
  }
`

css`
  :host {
    display: block;
    width: 100%;
  }

  /* 手风琴容器 */
  .skill-accordion {
    background-color: var(--skill-accordion-bg, transparent);
    border-radius: var(--skill-accordion-border-radius, var(--skill-radius-lg, 8px));
    overflow: hidden;
  }

  .skill-accordion--bordered {
    border: 1px solid var(--skill-accordion-border-color, var(--skill-gray-200));
    background-color: var(--skill-accordion-bg, #fff);
  }

  .skill-accordion--ghost {
    background-color: transparent;
    border: none;
  }

  .skill-accordion--sm .skill-accordion__item {
    font-size: var(--skill-text-sm, 14px);
  }

  .skill-accordion--sm .skill-accordion__header {
    padding: var(--skill-accordion-header-padding-sm, 12px 16px);
    min-height: var(--skill-accordion-header-height-sm, 40px);
  }

  .skill-accordion--sm .skill-accordion__content {
    padding: var(--skill-accordion-content-padding-sm, 12px 16px);
    font-size: var(--skill-text-sm, 14px);
  }

  .skill-accordion--md .skill-accordion__item {
    font-size: var(--skill-text-base, 16px);
  }

  .skill-accordion--md .skill-accordion__header {
    padding: var(--skill-accordion-header-padding-md, 16px 20px);
    min-height: var(--skill-accordion-header-height-md, 48px);
  }

  .skill-accordion--md .skill-accordion__content {
    padding: var(--skill-accordion-content-padding-md, 16px 20px);
    font-size: var(--skill-text-base, 16px);
  }

  .skill-accordion--lg .skill-accordion__item {
    font-size: var(--skill-text-lg, 18px);
  }

  .skill-accordion--lg .skill-accordion__header {
    padding: var(--skill-accordion-header-padding-lg, 20px 24px);
    min-height: var(--skill-accordion-header-height-lg, 56px);
  }

  .skill-accordion--lg .skill-accordion__content {
    padding: var(--skill-accordion-content-padding-lg, 20px 24px);
    font-size: var(--skill-text-lg, 18px);
  }

  /* 手风琴项目 */
  .skill-accordion__item {
    border-bottom: 1px solid var(--skill-accordion-item-border-color, var(--skill-gray-200));
    background-color: var(--skill-accordion-item-bg, #fff);
    transition: all var(--skill-duration-normal, 0.3s) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  .skill-accordion__item:last-child {
    border-bottom: none;
  }

  .skill-accordion__item--disabled {
    opacity: 0.6;
    pointer-events: none;
  }

  .skill-accordion--ghost .skill-accordion__item {
    background-color: transparent;
    border-bottom-color: var(--skill-accordion-ghost-border-color, var(--skill-gray-200));
  }

  .skill-accordion--bordered .skill-accordion__item:first-child {
    border-radius: var(--skill-accordion-border-radius, var(--skill-radius-lg, 8px)) var(--skill-accordion-border-radius, var(--skill-radius-lg, 8px)) 0 0;
  }

  .skill-accordion--bordered .skill-accordion__item:last-child {
    border-radius: 0 0 var(--skill-accordion-border-radius, var(--skill-radius-lg, 8px)) var(--skill-accordion-border-radius, var(--skill-radius-lg, 8px));
  }

  /* 手风琴头部 */
  .skill-accordion__header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    cursor: pointer;
    user-select: none;
    outline: none;
    transition: all var(--skill-duration-fast, 0.2s) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
    position: relative;
    color: var(--skill-accordion-header-color, var(--skill-gray-900));
    font-weight: var(--skill-accordion-header-font-weight, 500);
    line-height: var(--skill-accordion-header-line-height, 1.5);
  }

  .skill-accordion__header:hover {
    background-color: var(--skill-accordion-header-hover-bg, var(--skill-gray-50));
  }

  .skill-accordion__header:focus {
    background-color: var(--skill-accordion-header-focus-bg, var(--skill-primary-50));
    box-shadow: var(--skill-accordion-header-focus-shadow, inset 0 0 0 2px var(--skill-primary-200));
  }

  .skill-accordion--ghost .skill-accordion__header:hover {
    background-color: var(--skill-accordion-ghost-header-hover-bg, var(--skill-gray-100));
  }

  /* 头部内容 */
  .skill-accordion__header-content {
    flex: 1;
    display: flex;
    align-items: center;
    gap: var(--skill-accordion-header-content-gap, 12px);
  }

  .skill-accordion__header-title {
    flex: 1;
    margin: 0;
  }

  .skill-accordion__header-extra {
    color: var(--skill-accordion-header-extra-color, var(--skill-gray-500));
    font-size: var(--skill-accordion-header-extra-font-size, 14px);
  }

  /* 箭头图标 */
  .skill-accordion__arrow {
    display: flex;
    align-items: center;
    justify-content: center;
    width: var(--skill-accordion-arrow-size, 20px);
    height: var(--skill-accordion-arrow-size, 20px);
    color: var(--skill-accordion-arrow-color, var(--skill-gray-500));
    transition: transform var(--skill-duration-normal, 0.3s) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
    flex-shrink: 0;
  }

  .skill-accordion__arrow svg {
    width: 100%;
    height: 100%;
    fill: currentColor;
  }

  .skill-accordion__item--active .skill-accordion__arrow {
    transform: rotate(180deg);
    color: var(--skill-accordion-arrow-active-color, var(--skill-primary-500));
  }

  /* 箭头位置 */
  .skill-accordion--icon-left .skill-accordion__header-content {
    order: 1;
  }

  .skill-accordion--icon-left .skill-accordion__arrow {
    order: 0;
    margin-right: var(--skill-accordion-arrow-margin, 8px);
  }

  .skill-accordion--icon-right .skill-accordion__header-content {
    order: 0;
  }

  .skill-accordion--icon-right .skill-accordion__arrow {
    order: 1;
    margin-left: var(--skill-accordion-arrow-margin, 8px);
  }

  /* 手风琴内容 */
  .skill-accordion__content {
    background-color: var(--skill-accordion-content-bg, var(--skill-gray-50));
    color: var(--skill-accordion-content-color, var(--skill-gray-700));
    border-top: 1px solid var(--skill-accordion-content-border-color, var(--skill-gray-200));
    overflow: hidden;
    transition: all var(--skill-duration-normal, 0.3s) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
    max-height: 0;
    opacity: 0;
  }

  .skill-accordion--ghost .skill-accordion__content {
    background-color: transparent;
    border-top-color: var(--skill-accordion-ghost-content-border-color, var(--skill-gray-200));
  }

  .skill-accordion__item--active .skill-accordion__content {
    max-height: var(--skill-accordion-content-max-height, 2000px);
    opacity: 1;
  }

  .skill-accordion__content-inner {
    padding: var(--skill-accordion-content-padding, 16px 20px);
  }

  /* 无边框模式下的间距调整 */
  .skill-accordion--ghost .skill-accordion__item {
    margin-bottom: var(--skill-accordion-ghost-item-margin, 8px);
    border-radius: var(--skill-accordion-ghost-item-radius, var(--skill-radius-md, 6px));
    border: 1px solid var(--skill-accordion-ghost-item-border-color, var(--skill-gray-200));
  }

  .skill-accordion--ghost .skill-accordion__item:last-child {
    margin-bottom: 0;
  }

  /* 动画效果 */
  .skill-accordion__content-inner {
    animation: skill-accordion-fadeIn var(--skill-duration-normal, 0.3s) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  @keyframes skill-accordion-fadeIn {
    from {
      opacity: 0;
      transform: translateY(-8px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  /* 焦点样式 */
  .skill-accordion__header:focus-visible {
    outline: 2px solid var(--skill-primary-500);
    outline-offset: -2px;
  }

  /* 加载状态 */
  .skill-accordion__content-loading {
    display: flex;
    align-items: center;
    justify-content: center;
    padding: var(--skill-accordion-content-padding, 16px 20px);
    color: var(--skill-gray-500);
    font-size: 14px;
  }

  .skill-accordion__content-loading::after {
    content: '';
    width: 16px;
    height: 16px;
    border: 2px solid var(--skill-gray-300);
    border-top-color: var(--skill-primary-500);
    border-radius: 50%;
    animation: skill-accordion-spin 1s linear infinite;
    margin-left: 8px;
  }

  @keyframes skill-accordion-spin {
    to {
      transform: rotate(360deg);
    }
  }

  /* 错误状态 */
  .skill-accordion__content-error {
    padding: var(--skill-accordion-content-padding, 16px 20px);
    background-color: var(--skill-error-50, #FEE6E7);
    color: var(--skill-error-600, #DC2626);
    border-top: 1px solid var(--skill-error-200, #FCB3B4);
  }

  /* 自定义箭头样式 */
  .skill-accordion__arrow--custom {
    width: auto;
    height: auto;
  }

  /* 大尺寸下的调整 */
  @media (min-width: 768px) {
    .skill-accordion--lg .skill-accordion__content-inner {
      padding: var(--skill-accordion-content-padding-lg, 24px 28px);
    }
  }

  /* 暗色主题支持 */
  .skill-accordion--dark {
    --skill-accordion-bg: var(--skill-gray-800);
    --skill-accordion-item-bg: var(--skill-gray-700);
    --skill-accordion-header-color: var(--skill-gray-100);
    --skill-accordion-content-color: var(--skill-gray-300);
    --skill-accordion-item-border-color: var(--skill-gray-600);
    --skill-accordion-content-border-color: var(--skill-gray-600);
    --skill-accordion-arrow-color: var(--skill-gray-400);
    --skill-accordion-header-hover-bg: var(--skill-gray-600);
    --skill-accordion-content-bg: var(--skill-gray-800);
  }

  /* 无障碍支持 */
  .skill-accordion__header[aria-expanded="true"] {
    font-weight: 600;
  }

  .skill-accordion__header[aria-disabled="true"] {
    cursor: not-allowed;
    opacity: 0.5;
  }
`

css`
  :host {
    display: inline-block;
    position: relative;
    --skill-tooltip-bg: var(--skill-gray-900, #212529);
    --skill-tooltip-color: var(--skill-white, #ffffff);
    --skill-tooltip-border: var(--skill-gray-700, #495057);
    --skill-tooltip-shadow: 0 4px 12px rgba(0, 0, 0, 0.15);
    --skill-tooltip-border-radius: var(--skill-border-radius-md, 6px);
    --skill-tooltip-font-size: var(--skill-font-body-4, 12px);
    --skill-tooltip-line-height: var(--skill-line-height-sm, 1.4);
    --skill-tooltip-padding: var(--skill-spacing-sm, 8px) var(--skill-spacing-md, 12px);
    --skill-tooltip-max-width: var(--skill-tooltip-max-width, 300px);
    --skill-tooltip-transition: var(--skill-transition-fast, 0.2s);
    --skill-tooltip-z-index: 1000;
    --skill-tooltip-arrow-size: 8px;
  }

  /* Variant styles */
  :host([variant='dark']) {
    --skill-tooltip-bg: var(--skill-gray-900, #212529);
    --skill-tooltip-color: var(--skill-white, #ffffff);
    --skill-tooltip-border: var(--skill-gray-700, #495057);
  }

  :host([variant='light']) {
    --skill-tooltip-bg: var(--skill-white, #ffffff);
    --skill-tooltip-color: var(--skill-gray-900, #212529);
    --skill-tooltip-border: var(--skill-gray-300, #dee2e6);
    --skill-tooltip-shadow: 0 4px 12px rgba(0, 0, 0, 0.08);
  }

  :host([variant='success']) {
    --skill-tooltip-bg: var(--skill-success-500, #28a745);
    --skill-tooltip-color: var(--skill-white, #ffffff);
    --skill-tooltip-border: var(--skill-success-600, #218838);
  }

  :host([variant='warning']) {
    --skill-tooltip-bg: var(--skill-warning-500, #ffc107);
    --skill-tooltip-color: var(--skill-gray-900, #212529);
    --skill-tooltip-border: var(--skill-warning-600, #e0a800);
  }

  :host([variant='error']) {
    --skill-tooltip-bg: var(--skill-error-500, #dc3545);
    --skill-tooltip-color: var(--skill-white, #ffffff);
    --skill-tooltip-border: var(--skill-error-600, #c82333);
  }

  :host([variant='info']) {
    --skill-tooltip-bg: var(--skill-info-500, #17a2b8);
    --skill-tooltip-color: var(--skill-white, #ffffff);
    --skill-tooltip-border: var(--skill-info-600, #138496);
  }

  /* Container */
  .skill-tooltip__container {
    position: relative;
    display: inline-block;
  }

  /* Trigger element wrapper */
  .skill-tooltip__trigger {
    display: inline-block;
    cursor: help;
  }

  /* Tooltip container */
  .skill-tooltip__tooltip {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: var(--skill-tooltip-z-index);
    max-width: var(--skill-tooltip-max-width);
    padding: var(--skill-tooltip-padding);
    background-color: var(--skill-tooltip-bg);
    color: var(--skill-tooltip-color);
    border: 1px solid var(--skill-tooltip-border);
    border-radius: var(--skill-tooltip-border-radius);
    box-shadow: var(--skill-tooltip-shadow);
    font-size: var(--skill-tooltip-font-size);
    line-height: var(--skill-tooltip-line-height);
    font-weight: var(--skill-font-normal, 400);
    text-align: left;
    word-wrap: break-word;
    pointer-events: none;
    opacity: 0;
    visibility: hidden;
    transition: all var(--skill-tooltip-transition);
    transform-origin: center;
  }

  /* Visible state */
  .skill-tooltip__tooltip:not([hidden]) {
    opacity: 1;
    visibility: visible;
    pointer-events: auto;
  }

  /* Show/Hide animations */
  @keyframes tooltipFadeIn {
    from {
      opacity: 0;
      transform: translate(-50%, -50%) scale(0.8);
    }
    to {
      opacity: 1;
      transform: translate(-50%, -50%) scale(1);
    }
  }

  @keyframes tooltipFadeOut {
    from {
      opacity: 1;
      transform: translate(-50%, -50%) scale(1);
    }
    to {
      opacity: 0;
      transform: translate(-50%, -50%) scale(0.8);
    }
  }

  .skill-tooltip__tooltip:not([hidden]) {
    animation: tooltipFadeIn 0.2s ease-out forwards;
  }

  .skill-tooltip__tooltip[hidden] {
    animation: tooltipFadeOut 0.15s ease-out forwards;
  }

  /* Content wrapper */
  .skill-tooltip__content {
    display: block;
    max-width: 100%;
    overflow-wrap: break-word;
  }

  /* Arrow */
  .skill-tooltip__arrow {
    position: absolute;
    width: 0;
    height: 0;
    border: var(--skill-tooltip-arrow-size) solid transparent;
    transition: all var(--skill-tooltip-transition);
  }

  /* Arrow colors */
  .skill-tooltip__arrow {
    border-top-color: var(--skill-tooltip-bg);
    border-right-color: var(--skill-tooltip-bg);
    border-bottom-color: var(--skill-tooltip-bg);
    border-left-color: var(--skill-tooltip-bg);
  }

  /* Position-based arrow positioning */
  .skill-tooltip__tooltip[data-position="top"] .skill-tooltip__arrow {
    bottom: calc(var(--skill-tooltip-arrow-size) * -2);
    left: 50%;
    transform: translateX(-50%) rotate(180deg);
  }

  .skill-tooltip__tooltip[data-position="bottom"] .skill-tooltip__arrow {
    top: calc(var(--skill-tooltip-arrow-size) * -2);
    left: 50%;
    transform: translateX(-50%) rotate(0deg);
  }

  .skill-tooltip__tooltip[data-position="left"] .skill-tooltip__arrow {
    right: calc(var(--skill-tooltip-arrow-size) * -2);
    top: 50%;
    transform: translateY(-50%) rotate(270deg);
  }

  .skill-tooltip__tooltip[data-position="right"] .skill-tooltip__arrow {
    left: calc(var(--skill-tooltip-arrow-size) * -2);
    top: 50%;
    transform: translateY(-50%) rotate(90deg);
  }

  /* Size variants */
  :host([size='sm']) {
    --skill-tooltip-font-size: var(--skill-font-body-5, 10px);
    --skill-tooltip-padding: var(--skill-spacing-2xs, 4px) var(--skill-spacing-sm, 8px);
    --skill-tooltip-arrow-size: 6px;
  }

  :host([size='lg']) {
    --skill-tooltip-font-size: var(--skill-font-body-3, 14px);
    --skill-tooltip-padding: var(--skill-spacing-md, 12px) var(--skill-spacing-lg, 16px);
    --skill-tooltip-arrow-size: 10px;
  }

  :host([size='xl']) {
    --skill-tooltip-font-size: var(--skill-font-body-2, 16px);
    --skill-tooltip-padding: var(--skill-spacing-lg, 16px) var(--skill-spacing-xl, 20px);
    --skill-tooltip-arrow-size: 12px;
  }

  /* Position-specific adjustments */
  :host([position='top']) .skill-tooltip__tooltip {
    transform: translate(-50%, -100%) translateY(-8px);
  }

  :host([position='bottom']) .skill-tooltip__tooltip {
    transform: translate(-50%, 0%) translateY(8px);
  }

  :host([position='left']) .skill-tooltip__tooltip {
    transform: translate(-100%, -50%) translateX(-8px);
  }

  :host([position='right']) .skill-tooltip__tooltip {
    transform: translate(0%, -50%) translateX(8px);
  }

  /* Trigger styles */
  :host([trigger='click']) .skill-tooltip__trigger {
    cursor: pointer;
  }

  :host([trigger='focus']) .skill-tooltip__trigger {
    cursor: default;
  }

  /* Disabled state */
  :host([disabled]) {
    pointer-events: none;
  }

  :host([disabled]) .skill-tooltip__trigger {
    cursor: not-allowed;
    opacity: 0.6;
  }

  /* Focus styles for accessibility */
  .skill-tooltip__trigger:focus-within {
    outline: 2px solid var(--skill-primary-500, #0066cc);
    outline-offset: 2px;
    border-radius: var(--skill-border-radius-sm, 4px);
  }

  /* High contrast mode support */
  @media (prefers-contrast: high) {
    .skill-tooltip__tooltip {
      border-width: 2px;
    }

    :host([variant='light']) .skill-tooltip__tooltip {
      background: Window;
      color: WindowText;
      border: 2px solid WindowText;
    }

    :host([variant='dark']) .skill-tooltip__tooltip {
      background: WindowText;
      color: Window;
      border: 2px solid WindowText;
    }
  }

  /* Reduced motion support */
  @media (prefers-reduced-motion: reduce) {
    .skill-tooltip__tooltip {
      transition: opacity 0.1s;
    }

    .skill-tooltip__tooltip:not([hidden]) {
      animation: none;
    }

    .skill-tooltip__tooltip[hidden] {
      animation: none;
    }
  }

  /* Dark mode support */
  @media (prefers-color-scheme: dark) {
    :host {
      --skill-tooltip-bg: var(--skill-gray-800, #343a40);
      --skill-tooltip-color: var(--skill-gray-100, #f8f9fa);
      --skill-tooltip-border: var(--skill-gray-600, #6c757d);
    }

    :host([variant='light']) {
      --skill-tooltip-bg: var(--skill-gray-100, #f8f9fa);
      --skill-tooltip-color: var(--skill-gray-900, #212529);
      --skill-tooltip-border: var(--skill-gray-300, #dee2e6);
    }
  }

  /* Print styles */
  @media print {
    .skill-tooltip__tooltip {
      display: none !important;
    }
  }

  /* Mobile-specific adjustments */
  @media (max-width: 768px) {
    .skill-tooltip__tooltip {
      --skill-tooltip-max-width: 250px;
      --skill-tooltip-font-size: var(--skill-font-body-4, 12px);
    }

    :host([position='left']) .skill-tooltip__tooltip,
    :host([position='right']) .skill-tooltip__tooltip {
      /* On mobile, prefer top/bottom positioning */
      transform: translate(-50%, -100%) translateY(-8px);
    }
  }

  /* Performance optimizations */
  .skill-tooltip__tooltip {
    will-change: opacity, transform;
    backface-visibility: hidden;
    perspective: 1000px;
  }

  .skill-tooltip__arrow {
    will-change: transform;
    backface-visibility: hidden;
  }
