      --timeline-card-bg: var(--skill-gray-800, #1F2937);
      --timeline-card-border: var(--skill-gray-700, #374151);
      --timeline-card-shadow: 0 1px 2px 0 rgba(0, 0, 0, 0.3);
    }

    .skill-timeline__title {
      --timeline-title-color: var(--skill-gray-100, #F3F4F6);
    }

    .skill-timeline__description {
      --timeline-description-color: var(--skill-gray-300, #D1D5DB);
    }

    .skill-timeline__time {
      --timeline-time-color: var(--skill-gray-400, #9CA3AF);
    }
  }

  /* Print styles */
  @media print {
    .skill-timeline__dot {
      border: 3px solid #000 !important;
      background: #FFF !important;
      print-color-adjust: exact;
      -webkit-print-color-adjust: exact;
    }

    .skill-timeline__dot svg,
    .skill-timeline__dot skill-icon {
      color: #000 !important;
    }

    .skill-timeline__line {
      background: #000 !important;
      print-color-adjust: exact;
      -webkit-print-color-adjust: exact;
    }

    .skill-timeline__card {
      border: 2px solid #000 !important;
      background: #FFF !important;
      box-shadow: none !important;
      print-color-adjust: exact;
      -webkit-print-color-adjust: exact;
    }

    .skill-timeline__title {
      color: #000 !important;
    }

    .skill-timeline__description {
      color: #333 !important;
    }

    .skill-timeline__time {
      color: #666 !important;
    }
  }

  /* Animation for new items */
  .skill-timeline__item {
    animation: timeline-item-fade-in var(--skill-duration-normal, 300ms) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  @keyframes timeline-item-fade-in {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  /* Loading state */
  .skill-timeline--loading .skill-timeline__dot::after {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    border: 2px solid transparent;
    border-top-color: rgba(255, 255, 255, 0.3);
    border-radius: var(--skill-radius-full, 9999px);
    animation: timeline-dot-spin 1s linear infinite;
  }

  @keyframes timeline-dot-spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  /* Hover effects */
  .skill-timeline__item:hover .skill-timeline__dot {
    transform: scale(1.1);
    box-shadow: 0 0 0 4px rgba(10, 89, 247, 0.1);
  }

  .skill-timeline__item:hover .skill-timeline__dot.skill-timeline__dot--success {
    box-shadow: 0 0 0 4px rgba(0, 212, 170, 0.1);
  }

  .skill-timeline__item:hover .skill-timeline__dot.skill-timeline__dot--warning {
    box-shadow: 0 0 0 4px rgba(255, 180, 0, 0.1);
  }

  .skill-timeline__item:hover .skill-timeline__dot.skill-timeline__dot--error {
    box-shadow: 0 0 0 4px rgba(250, 42, 45, 0.1);
  }

  .skill-timeline__item:hover .skill-timeline__dot.skill-timeline__dot--info {
    box-shadow: 0 0 0 4px rgba(10, 89, 247, 0.1);
  }

  /* Compact mode */
  :host([compact]) .skill-timeline__card {
    padding: var(--skill-spacing-sm, 12px);
  }

  :host([compact]) .skill-timeline__title {
    --timeline-title-font-size: var(--skill-font-size-body-2, 14px);
    margin-bottom: 2px;
  }

  :host([compact]) .skill-timeline__description {
    --timeline-description-font-size: var(--skill-font-size-body-3, 12px);
    margin-bottom: 4px;
  }

  /* Minimized mode */
  :host([minimal]) .skill-timeline__description,
  :host([minimal]) .skill-timeline__time {
    display: none;
  }

  :host([minimal]) .skill-timeline__title {
    --timeline-title-font-size: var(--skill-font-size-body-3, 12px);
    margin-bottom: 0;
  }

  /* Icon-only mode */
  :host([icon-only]) .skill-timeline__content {
    display: none;
  }

  :host([icon-only]) .skill-timeline__item {
    justify-content: center;
  }

  /* Alternating background */
  .skill-timeline--alternate .skill-timeline__item:nth-child(even) .skill-timeline__card {
    background: var(--timeline-card-alt-bg, var(--skill-gray-50, #F9FAFB));
  }

  /* Pulse animation for active items */
  .skill-timeline__item--active .skill-timeline__dot {
    animation: timeline-dot-pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes timeline-dot-pulse {
    0%, 100% {
      box-shadow: 0 0 0 0 rgba(10, 89, 247, 0.4);
    }
    50% {
      box-shadow: 0 0 0 8px rgba(10, 89, 247, 0);
    }
  }

  /* Progress indicator */
  .skill-timeline__progress {
    position: absolute;
    top: 0;
    left: calc(var(--timeline-dot-size, 16px) / 2);
    width: var(--timeline-line-width, 2px);
    background: var(--skill-primary-500, #0A59F7);
    transform: translateX(-50%);
    z-index: 1;
    transition: height var(--skill-duration-normal, 300ms) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  .skill-timeline--horizontal .skill-timeline__progress {
    top: calc(var(--timeline-dot-size, 16px) / 2);
    left: 0;
    width: var(--timeline-line-width, 2px);
    height: var(--timeline-line-width, 2px);
    transform: translateY(-50%);
    transition: width var(--skill-duration-normal, 300ms) var(--skill-ease-out, cubic-bezier(0, 0, 0.2, 1));
  }

  /* Grouped timeline */
  .skill-timeline--grouped .skill-timeline__item {
    margin-bottom: var(--timeline-group-gap, var(--skill-spacing-xl, 24px));
  }

  .skill-timeline--grouped .skill-timeline__item::before {
    content: attr(data-group);
    position: absolute;
    top: -20px;
    left: 0;
    font-size: var(--skill-font-size-body-3, 12px);
    font-weight: var(--skill-font-weight-semibold, 600);
    color: var(--skill-gray-500, #6B7280);
    text-transform: uppercase;
    letter-spacing: 0.05em;
  }
`

css`
  /* ===== Host 元素 ===== */
  :host {
    display: block;
    width: 100%;
    box-sizing: border-box;
    font-family: var(--skill-font-family);
  }

  /* ===== 主容器 ===== */
  .validation-summary {
    background: var(--validation-summary-bg, var(--skill-gray-0));
    border: 1px solid var(--validation-summary-border, var(--skill-gray-200));
    border-radius: var(--validation-summary-radius, var(--skill-radius-lg));
    overflow: hidden;
    transition: all var(--skill-transition-duration-fast) var(--skill-transition-easing);
    position: relative;
  }

  /* ===== 容器状态样式 ===== */
  .validation-summary--error {
    border-color: var(--skill-error-300);
    background: var(--skill-error-50);
  }

  .validation-summary--warning {
    border-color: var(--skill-warning-300);
    background: var(--skill-warning-50);
  }

  .validation-summary--info {
    border-color: var(--skill-info-300);
    background: var(--skill-info-50);
  }

  .validation-summary--success {
    border-color: var(--skill-success-300);
    background: var(--skill-success-50);
  }

  /* ===== 头部样式 ===== */
  .validation-summary__header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: var(--skill-spacing-md) var(--skill-spacing-lg);
    border-bottom: 1px solid var(--skill-gray-100);
    background: var(--skill-gray-50);
    gap: var(--skill-spacing-md);
  }

  .validation-summary__title {
    display: flex;
    align-items: center;
    gap: var(--skill-spacing-sm);
    font-size: var(--skill-font-body-3);
    font-weight: var(--skill-font-weight-semibold);
    color: var(--skill-gray-900);
    margin: 0;
    flex: 1;
    min-width: 0;
  }

  /* ===== 计数徽章 ===== */
  .validation-summary__count {
    background: var(--skill-primary-600);
    color: var(--skill-gray-0);
    font-size: var(--skill-font-body-6);
    font-weight: var(--skill-font-weight-medium);
    padding: 2px 6px;
    border-radius: var(--skill-radius-full);
    min-width: 20px;
    height: 20px;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    flex-shrink: 0;
  }

  .validation-summary__count--error {
    background: var(--skill-error-600);
  }

  .validation-summary__count--warning {
    background: var(--skill-warning-600);
  }

  .validation-summary__count--info {
    background: var(--skill-info-600);
  }

  .validation-summary__count--success {
    background: var(--skill-success-600);
  }

  /* ===== 内容区域 ===== */
  .validation-summary__content {
    padding: var(--validation-summary-padding, var(--skill-spacing-md));
    max-height: 300px;
    overflow-y: auto;
    scrollbar-width: thin;
    scrollbar-color: var(--skill-gray-300) var(--skill-gray-100);
  }

  .validation-summary__content::-webkit-scrollbar {
    width: 6px;
  }

  .validation-summary__content::-webkit-scrollbar-track {
    background: var(--skill-gray-100);
  }

  .validation-summary__content::-webkit-scrollbar-thumb {
    background: var(--skill-gray-300);
    border-radius: 3px;
  }

  .validation-summary__content::-webkit-scrollbar-thumb:hover {
    background: var(--skill-gray-400);
  }

  /* ===== 消息项样式 ===== */
  .validation-summary__message {
    display: flex;
    align-items: flex-start;
    gap: var(--skill-spacing-sm);
    padding: var(--skill-spacing-sm);
    margin-bottom: var(--validation-summary-gap, var(--skill-spacing-xs));
    border-radius: var(--skill-radius-md);
    cursor: pointer;
    transition: all var(--skill-transition-duration-fast) var(--skill-transition-easing);
    border: 1px solid transparent;
    position: relative;
  }

  .validation-summary__message:hover {
    background: rgba(255, 255, 255, 0.5);
    border-color: var(--skill-gray-200);
    transform: translateX(2px);
  }

  .validation-summary__message:last-child {
    margin-bottom: 0;
  }

  /* ===== 消息状态样式 ===== */
  .validation-summary__message--error {
    background: rgba(239, 68, 68, 0.1);
    color: var(--skill-error-700);
  }

  .validation-summary__message--error:hover {
    background: rgba(239, 68, 68, 0.15);
  }

  .validation-summary__message--warning {
    background: rgba(245, 158, 11, 0.1);
    color: var(--skill-warning-700);
  }

  .validation-summary__message--warning:hover {
    background: rgba(245, 158, 11, 0.15);
  }

  .validation-summary__message--info {
    background: rgba(59, 130, 246, 0.1);
    color: var(--skill-info-700);
  }

  .validation-summary__message--info:hover {
    background: rgba(59, 130, 246, 0.15);
  }

  .validation-summary__message--success {
    background: rgba(34, 197, 94, 0.1);
    color: var(--skill-success-700);
  }

  .validation-summary__message--success:hover {
    background: rgba(34, 197, 94, 0.15);
  }

  /* ===== 消息图标 ===== */
  .validation-summary__message-icon {
    flex-shrink: 0;
    width: 16px;
    height: 16px;
    margin-top: 2px;
    opacity: 0.8;
  }

  /* ===== 消息内容 ===== */
  .validation-summary__message-content {
    flex: 1;
    min-width: 0;
  }

  .validation-summary__message-field {
    font-weight: var(--skill-font-weight-medium);
    font-size: var(--skill-font-body-4);
    margin-bottom: 2px;
    display: flex;
    align-items: center;
    gap: var(--skill-spacing-xs);
    flex-wrap: wrap;
  }

  .validation-summary__message-text {
    font-size: var(--skill-font-body-4);
    line-height: 1.4;
    word-break: break-word;
  }

  /* ===== 消息关闭按钮 ===== */
  .validation-summary__message-dismiss {
    flex-shrink: 0;
    background: none;
    border: none;
    padding: 2px;
    cursor: pointer;
    color: inherit;
    opacity: 0.6;
    border-radius: var(--skill-radius-sm);
    transition: all var(--skill-transition-duration-fast) var(--skill-transition-easing);
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .validation-summary__message-dismiss:hover {
    opacity: 1;
    background: rgba(0, 0, 0, 0.1);
  }

  .validation-summary__message-dismiss:active {
    transform: scale(0.95);
  }

  /* ===== 空状态样式 ===== */
  .validation-summary__empty-state {
    padding: var(--skill-spacing-xl) var(--skill-spacing-lg);
    text-align: center;
    color: var(--skill-gray-500);
    font-size: var(--skill-font-body-4);
  }

  .validation-summary__empty-state-icon {
    width: 48px;
    height: 48px;
    margin: 0 auto var(--skill-spacing-md);
    opacity: 0.4;
    color: var(--skill-gray-400);
  }

  /* ===== 底部样式 ===== */
  .validation-summary__footer {
    padding: var(--skill-spacing-sm) var(--skill-spacing-lg);
    border-top: 1px solid var(--skill-gray-100);
    background: var(--skill-gray-50);
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: var(--skill-spacing-md);
  }

  .validation-summary__actions {
    display: flex;
    gap: var(--skill-spacing-sm);
    align-items: center;
  }

  .validation-summary__summary {
    font-size: var(--skill-font-body-6);
    color: var(--skill-gray-600);
    flex: 1;
  }

  /* ===== 尺寸变体 ===== */

  /* 小尺寸 */
  .validation-summary--sm .validation-summary__message {
    padding: var(--skill-spacing-xs);
  }

  .validation-summary--sm .validation-summary__header {
    padding: var(--skill-spacing-sm) var(--skill-spacing-md);
  }

  .validation-summary--sm .validation-summary__footer {
    padding: var(--skill-spacing-xs) var(--skill-spacing-md);
  }

  /* 大尺寸 */
  .validation-summary--lg .validation-summary__message {
    padding: var(--skill-spacing-md);
  }

  .validation-summary--lg .validation-summary__header {
    padding: var(--skill-spacing-lg) var(--skill-spacing-xl);
  }

  .validation-summary--lg .validation-summary__footer {
    padding: var(--skill-spacing-md) var(--skill-spacing-xl);
  }

  /* ===== 优先级徽章 ===== */
  .validation-summary__priority {
    display: inline-flex;
    align-items: center;
    gap: 2px;
    font-size: var(--skill-font-body-6);
    font-weight: var(--skill-font-weight-medium);
    padding: 1px 4px;
    border-radius: var(--skill-radius-sm);
    text-transform: uppercase;
    letter-spacing: 0.5px;
  }

  .validation-summary__priority--high {
    background: var(--skill-error-100);
    color: var(--skill-error-700);
  }

  .validation-summary__priority--medium {
    background: var(--skill-warning-100);
    color: var(--skill-warning-700);
  }

  .validation-summary__priority--low {
    background: var(--skill-info-100);
    color: var(--skill-info-700);
  }

  /* ===== 消息动画 ===== */
  .validation-summary__message--dismissing {
    animation: validationMessageSlideOut 0.3s var(--skill-transition-easing) forwards;
  }

  @keyframes validationMessageSlideOut {
    from {
      opacity: 1;
      transform: translateX(0);
      max-height: 100px;
    }
    to {
      opacity: 0;
      transform: translateX(100%);
      max-height: 0;
      padding: 0;
      margin: 0;
    }
  }

  /* ===== 加载状态 ===== */
  .validation-summary--loading {
    pointer-events: none;
    opacity: 0.7;
    position: relative;
  }

  .validation-summary--loading::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 2px;
    background: linear-gradient(90deg, transparent, var(--skill-primary-500), transparent);
    animation: validationSummaryLoading 1.5s linear infinite;
    z-index: 1;
  }

  @keyframes validationSummaryLoading {
    0% {
      transform: translateX(-100%);
    }
    100% {
      transform: translateX(100%);
    }
  }

  /* ===== 可访问性焦点样式 ===== */
  .validation-summary__message:focus {
    outline: 2px solid var(--skill-primary-500);
    outline-offset: 2px;
  }

  .validation-summary__message-dismiss:focus-visible {
    outline: 2px solid var(--skill-primary-500);
    outline-offset: 1px;
  }

  /* ===== 响应式设计 ===== */
  @media (max-width: 768px) {
    .validation-summary__header,
    .validation-summary__content,
    .validation-summary__footer {
      padding-left: var(--skill-spacing-md);
      padding-right: var(--skill-spacing-md);
    }

    .validation-summary__footer {
      flex-direction: column;
      gap: var(--skill-spacing-sm);
      align-items: stretch;
    }

    .validation-summary__actions {
      justify-content: center;
    }

    .validation-summary__message-field {
      flex-direction: column;
      align-items: flex-start;
      gap: 2px;
    }

    .validation-summary__title {
      font-size: var(--skill-font-body-4);
    }

    .validation-summary__content {
      max-height: 250px;
    }
  }

  @media (max-width: 480px) {
    .validation-summary__header,
    .validation-summary__content,
    .validation-summary__footer {
      padding-left: var(--skill-spacing-sm);
      padding-right: var(--skill-spacing-sm);
    }

    .validation-summary__message {
      padding: var(--skill-spacing-xs);
    }

    .validation-summary__empty-state {
      padding: var(--skill-spacing-lg) var(--skill-spacing-md);
    }

    .validation-summary__empty-state-icon {
      width: 36px;
      height: 36px;
    }

    .validation-summary__content {
      max-height: 200px;
    }
  }

  /* ===== 高对比度模式支持 ===== */
  @media (prefers-contrast: high) {
    .validation-summary {
      border-width: 2px;
    }

    .validation-summary__message {
      border-width: 1px;
    }

    .validation-summary__message:focus {
      outline-width: 3px;
    }

    .validation-summary__count {
      border: 1px solid currentColor;
    }
  }

  /* ===== 深色模式支持 ===== */
  @media (prefers-color-scheme: dark) {
    .validation-summary {
      background: var(--skill-gray-800);
      border-color: var(--skill-gray-600);
    }

    .validation-summary__header {
      background: var(--skill-gray-900);
      border-color: var(--skill-gray-700);
    }

    .validation-summary__footer {
      background: var(--skill-gray-900);
      border-color: var(--skill-gray-700);
    }

    .validation-summary__title {
      color: var(--skill-gray-100);
    }

    .validation-summary__message:hover {
      background: rgba(255, 255, 255, 0.1);
      border-color: var(--skill-gray-500);
    }

    .validation-summary__message--error {
      background: rgba(239, 68, 68, 0.2);
    }

    .validation-summary__message--warning {
      background: rgba(245, 158, 11, 0.2);
    }

    .validation-summary__message--info {
      background: rgba(59, 130, 246, 0.2);
    }

    .validation-summary__message--success {
      background: rgba(34, 197, 94, 0.2);
    }

    .validation-summary__empty-state {
      color: var(--skill-gray-400);
    }

    .validation-summary__summary {
      color: var(--skill-gray-400);
    }

    .validation-summary--error {
      border-color: var(--skill-error-600);
      background: rgba(239, 68, 68, 0.1);
    }

    .validation-summary--warning {
      border-color: var(--skill-warning-600);
      background: rgba(245, 158, 11, 0.1);
    }

    .validation-summary--info {
      border-color: var(--skill-info-600);
      background: rgba(59, 130, 246, 0.1);
    }

    .validation-summary--success {
      border-color: var(--skill-success-600);
      background: rgba(34, 197, 94, 0.1);
    }
  }

  /* ===== 减少动画偏好支持 ===== */
  @media (prefers-reduced-motion: reduce) {
    .validation-summary,
    .validation-summary__message,
    .validation-summary__message-dismiss {
      transition: none;
    }

    .validation-summary__message:hover {
      transform: none;
    }

    .validation-summary__message--dismissing {
      animation: none;
      opacity: 0;
      max-height: 0;
    }

    .validation-summary--loading::after {
      animation: none;
    }
  }

  /* ===== 打印样式 ===== */
  @media print {
    .validation-summary {
      border: 1px solid #000;
      background: #fff !important;
      color: #000 !important;
      break-inside: avoid;
    }

    .validation-summary__message-dismiss {
      display: none;
    }

    .validation-summary--loading::after {
      display: none;
    }

    .validation-summary__content {
      max-height: none;
      overflow: visible;
    }
  }
`

css`
  :host {
    display: flex;
    align-items: center;
    width: 100%;
    --toolbar-height: 56px;
    --toolbar-padding: 0 var(--skill-spacing-md, 16px);
    --toolbar-bg: var(--skill-white, #ffffff);
    --toolbar-border: 1px solid var(--skill-neutral-200, #e5e7eb);
    --toolbar-border-radius: var(--skill-radius-md, 6px);
    --toolbar-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
    --toolbar-item-size: 40px;
    --toolbar-gap: var(--skill-spacing-xs, 4px);
    --toolbar-text-color: var(--skill-text-primary, #1f2937);
    --toolbar-icon-color: var(--skill-neutral-600, #4b5563);
    --toolbar-hover-bg: var(--skill-neutral-100, #f3f4f6);
    --toolbar-active-bg: var(--skill-primary-50, #eff6ff);
    --toolbar-disabled-color: var(--skill-neutral-400, #9ca3af);
  }

  .skill-toolbar {
    font-family: var(--skill-font-family, inherit);
    font-size: var(--skill-font-body-2, 14px);
    line-height: 1.5;
    height: var(--toolbar-height);
    padding: var(--toolbar-padding);
    background-color: var(--toolbar-bg);
    border: var(--toolbar-border);
    border-radius: var(--toolbar-border-radius);
    box-shadow: var(--toolbar-shadow);
    display: flex;
    align-items: center;
    position: relative;
    overflow: hidden;
  }

  /* 布局变体 */
  .skill-toolbar--horizontal {
    flex-direction: row;
  }

  .skill-toolbar--vertical {
    flex-direction: column;
    height: auto;
    width: var(--toolbar-height);
    padding: var(--skill-spacing-md, 16px) 0;
  }

  /* 对齐方式 */
  .skill-toolbar--left {
    justify-content: flex-start;
  }

  .skill-toolbar--center {
    justify-content: center;
  }

  .skill-toolbar--right {
    justify-content: flex-end;
  }

  .skill-toolbar--space-between {
    justify-content: space-between;
  }

  .skill-toolbar--space-around {
    justify-content: space-around;
  }

  /* 工具栏区域 */
  .skill-toolbar__left,
  .skill-toolbar__center,
  .skill-toolbar__right {
    display: flex;
    align-items: center;
    flex-shrink: 0;
  }

  .skill-toolbar--horizontal .skill-toolbar__left {
    margin-right: auto;
  }

  .skill-toolbar--horizontal .skill-toolbar__right {
    margin-left: auto;
  }

  .skill-toolbar--horizontal .skill-toolbar__center {
    margin: 0 var(--skill-spacing-md, 16px);
  }

  .skill-toolbar--vertical .skill-toolbar__left {
    margin-bottom: auto;
  }

  .skill-toolbar--vertical .skill-toolbar__right {
    margin-top: auto;
  }

  .skill-toolbar--vertical .skill-toolbar__center {
    margin: var(--skill-spacing-md, 16px) 0;
  }

  /* 工具栏项 */
  .skill-toolbar__item {
    display: flex;
    align-items: center;
    justify-content: center;
    min-width: var(--toolbar-item-size);
    min-height: var(--toolbar-item-size);
    padding: var(--skill-spacing-xs, 4px) var(--skill-spacing-sm, 8px);
    border-radius: var(--skill-radius-sm, 4px);
    color: var(--toolbar-text-color);
    text-decoration: none;
    cursor: pointer;
    transition: all 0.2s ease;
    border: none;
    background: none;
    font: inherit;
    position: relative;
    gap: var(--skill-spacing-xs, 4px);
  }

  .skill-toolbar__item:hover {
    background-color: var(--toolbar-hover-bg);
    color: var(--toolbar-text-color);
    text-decoration: none;
  }

  .skill-toolbar__item:focus {
    outline: 2px solid var(--skill-primary-500, #3b82f6);
    outline-offset: 2px;
  }

  .skill-toolbar__item--active {
    background-color: var(--toolbar-active-bg);
    color: var(--skill-primary-700, #1d4ed8);
    font-weight: 500;
  }

  .skill-toolbar__item--disabled {
    opacity: 0.6;
    cursor: not-allowed;
    pointer-events: none;
    color: var(--toolbar-disabled-color);
  }

  .skill-toolbar__item--icon-only {
    width: var(--toolbar-item-size);
    height: var(--toolbar-item-size);
    padding: 0;
  }

  .skill-toolbar__item--text-only {
    padding: var(--skill-spacing-xs, 6px) var(--skill-spacing-md, 16px);
    min-height: auto;
    font-size: var(--skill-font-body-2, 14px);
    font-weight: 500;
  }

  /* 图标 */
  .skill-toolbar__icon {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 20px;
    height: 20px;
    color: var(--toolbar-icon-color);
    flex-shrink: 0;
  }

  .skill-toolbar__item:hover .skill-toolbar__icon {
    color: var(--toolbar-text-color);
  }

  .skill-toolbar__item--active .skill-toolbar__icon {
    color: var(--skill-primary-600, #2563eb);
  }

  .skill-toolbar__icon svg {
    width: 100%;
    height: 100%;
    fill: currentColor;
  }

  /* 文本 */
  .skill-toolbar__text {
    font-size: var(--skill-font-body-2, 14px);
    font-weight: 500;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    flex: 1;
    min-width: 0;
  }

  /* 徽章 */
  .skill-toolbar__badge {
    position: absolute;
    top: 6px;
    right: 6px;
    min-width: 16px;
    height: 16px;
    padding: 0 4px;
    background-color: var(--skill-error-500, #ef4444);
    color: var(--skill-white, #ffffff);
    font-size: 10px;
    font-weight: 600;
    border-radius: var(--skill-radius-full, 9999px);
    display: flex;
    align-items: center;
    justify-content: center;
    line-height: 1;
  }

  /* 分割线 */
  .skill-toolbar__divider {
    width: 1px;
    height: 24px;
    background-color: var(--skill-neutral-200, #e5e7eb);
    margin: 0 var(--skill-spacing-sm, 8px);
    flex-shrink: 0;
  }

  .skill-toolbar--vertical .skill-toolbar__divider {
    width: 24px;
    height: 1px;
    margin: var(--skill-spacing-sm, 8px) 0;
  }

  .skill-toolbar__divider--dashed {
    background-image: linear-gradient(to bottom, var(--skill-neutral-300, #d1d5db) 50%, transparent 50%);
    background-size: 2px 6px;
    background-repeat: repeat-y;
  }

  .skill-toolbar--vertical .skill-toolbar__divider--dashed {
    background-image: linear-gradient(to right, var(--skill-neutral-300, #d1d5db) 50%, transparent 50%);
    background-size: 6px 2px;
    background-repeat: repeat-x;
  }

  /* 搜索框 */
  .skill-toolbar__search {
    position: relative;
    display: flex;
    align-items: center;
    min-width: 200px;
    max-width: 400px;
  }

  .skill-toolbar__search-input {
    width: 100%;
    height: var(--toolbar-item-size);
    padding: 0 var(--skill-spacing-md, 16px) 0 40px;
    border: 1px solid var(--skill-neutral-300, #d1d5db);
    border-radius: var(--skill-radius-sm, 4px);
    background-color: var(--skill-white, #ffffff);
    font-size: var(--skill-font-body-2, 14px);
    transition: all 0.2s ease;
  }

  .skill-toolbar__search-input:focus {
    outline: none;
    border-color: var(--skill-primary-500, #3b82f6);
    box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.1);
  }

  .skill-toolbar__search-icon {
    position: absolute;
    left: var(--skill-spacing-sm, 8px);
    width: 16px;
    height: 16px;
    color: var(--toolbar-icon-color);
    pointer-events: none;
  }

  /* 下拉菜单 */
  .skill-toolbar__dropdown {
    position: relative;
  }

  .skill-toolbar__dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    min-width: 200px;
    background-color: var(--skill-white, #ffffff);
    border: 1px solid var(--skill-neutral-200, #e5e7eb);
    border-radius: var(--skill-radius-md, 6px);
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    z-index: 1000;
    opacity: 0;
    visibility: hidden;
    transform: translateY(-8px);
    transition: all 0.2s ease;
    max-height: 300px;
    overflow-y: auto;
  }

  .skill-toolbar__dropdown-menu--visible {
    opacity: 1;
    visibility: visible;
    transform: translateY(4px);
  }

  .skill-toolbar__dropdown-item {
    display: flex;
    align-items: center;
    padding: var(--skill-spacing-sm, 8px) var(--skill-spacing-md, 16px);
    color: var(--toolbar-text-color);
    text-decoration: none;
    cursor: pointer;
    transition: background-color 0.2s ease;
    border: none;
    background: none;
    font: inherit;
    width: 100%;
    text-align: left;
    gap: var(--skill-spacing-sm, 8px);
  }

  .skill-toolbar__dropdown-item:hover {
    background-color: var(--toolbar-hover-bg);
  }

  .skill-toolbar__dropdown-item--active {
    background-color: var(--toolbar-active-bg);
    color: var(--skill-primary-700, #1d4ed8);
    font-weight: 500;
  }

  .skill-toolbar__dropdown-item--disabled {
    opacity: 0.6;
    cursor: not-allowed;
    pointer-events: none;
  }

  /* 标签页 */
  .skill-toolbar__tabs {
    display: flex;
    align-items: center;
    gap: var(--skill-spacing-xs, 2px);
  }

  .skill-toolbar__tab {
    display: flex;
    align-items: center;
    padding: var(--skill-spacing-sm, 8px) var(--skill-spacing-md, 16px);
    border-radius: var(--skill-radius-sm, 4px) var(--skill-radius-sm, 4px) 0 0;
    border: none;
    background: none;
    color: var(--toolbar-text-color);
    cursor: pointer;
    transition: all 0.2s ease;
    font: inherit;
    font-weight: 500;
    position: relative;
  }

  .skill-toolbar__tab:hover {
    background-color: var(--toolbar-hover-bg);
  }

  .skill-toolbar__tab--active {
    background-color: var(--toolbar-active-bg);
    color: var(--skill-primary-700, #1d4ed8);
  }

  .skill-toolbar__tab--active::after {
    content: '';
    position: absolute;
    bottom: -1px;
    left: 0;
    right: 0;
    height: 2px;
    background-color: var(--skill-primary-500, #3b82f6);
  }

  /* 面包屑导航 */
  .skill-toolbar__breadcrumb {
    display: flex;
    align-items: center;
    gap: var(--skill-spacing-xs, 4px);
    font-size: var(--skill-font-body-2, 14px);
    color: var(--toolbar-text-color);
  }

  .skill-toolbar__breadcrumb-item {
    color: var(--toolbar-text-color);
    text-decoration: none;
    transition: color 0.2s ease;
  }

  .skill-toolbar__breadcrumb-item:hover {
    color: var(--skill-primary-600, #2563eb);
  }

  .skill-toolbar__breadcrumb-separator {
    color: var(--toolbar-icon-color);
    margin: 0 var(--skill-spacing-xs, 2px);
  }

  /* 颜色主题 */
  .skill-toolbar--primary {
    --toolbar-bg: var(--skill-primary-500, #3b82f6);
    --toolbar-text-color: var(--skill-white, #ffffff);
    --toolbar-icon-color: rgba(255, 255, 255, 0.8);
    --toolbar-hover-bg: rgba(255, 255, 255, 0.1);
    --toolbar-active-bg: rgba(255, 255, 255, 0.2);
    --toolbar-border: none;
  }

  .skill-toolbar--secondary {
    --toolbar-bg: var(--skill-neutral-100, #f3f4f6);
    --toolbar-border: 1px solid var(--skill-neutral-200, #e5e7eb);
    --toolbar-text-color: var(--skill-neutral-700, #374151);
    --toolbar-icon-color: var(--skill-neutral-600, #4b5563);
    --toolbar-hover-bg: var(--skill-neutral-200, #e5e7eb);
  }

  .skill-toolbar--success {
    --toolbar-bg: var(--skill-success-500, #10b981);
    --toolbar-text-color: var(--skill-white, #ffffff);
    --toolbar-icon-color: rgba(255, 255, 255, 0.8);
    --toolbar-hover-bg: rgba(255, 255, 255, 0.1);
    --toolbar-active-bg: rgba(255, 255, 255, 0.2);
    --toolbar-border: none;
  }

  .skill-toolbar--warning {
    --toolbar-bg: var(--skill-warning-500, #f59e0b);
    --toolbar-text-color: var(--skill-white, #ffffff);
    --toolbar-icon-color: rgba(255, 255, 255, 0.8);
    --toolbar-hover-bg: rgba(255, 255, 255, 0.1);
    --toolbar-active-bg: rgba(255, 255, 255, 0.2);
    --toolbar-border: none;
  }

  .skill-toolbar--error {
    --toolbar-bg: var(--skill-error-500, #ef4444);
    --toolbar-text-color: var(--skill-white, #ffffff);
    --toolbar-icon-color: rgba(255, 255, 255, 0.8);
    --toolbar-hover-bg: rgba(255, 255, 255, 0.1);
    --toolbar-active-bg: rgba(255, 255, 255, 0.2);
    --toolbar-border: none;
  }

  /* 尺寸变体 */
  .skill-toolbar--xs {
    --toolbar-height: 40px;
    --toolbar-item-size: 32px;
    --toolbar-padding: 0 var(--skill-spacing-sm, 8px);
  }

  .skill-toolbar--sm {
    --toolbar-height: 48px;
    --toolbar-item-size: 36px;
    --toolbar-padding: 0 var(--skill-spacing-md, 12px);
  }

  .skill-toolbar--md {
    --toolbar-height: 56px;
    --toolbar-item-size: 40px;
    --toolbar-padding: 0 var(--skill-spacing-md, 16px);
  }

  .skill-toolbar--lg {
    --toolbar-height: 64px;
    --toolbar-item-size: 44px;
    --toolbar-padding: 0 var(--skill-spacing-lg, 20px);
  }

  .skill-toolbar--xl {
    --toolbar-height: 72px;
    --toolbar-item-size: 48px;
    --toolbar-padding: 0 var(--skill-spacing-xl, 24px);
  }

  /* 紧凑模式 */
  .skill-toolbar--compact {
    --toolbar-gap: 2px;
    --toolbar-padding: 0 var(--skill-spacing-sm, 8px);
  }

  .skill-toolbar--compact .skill-toolbar__item {
    padding: var(--skill-spacing-xs, 2px) var(--skill-spacing-sm, 6px);
  }

  /* 边框样式 */
  .skill-toolbar--outlined {
    --toolbar-shadow: none;
    --toolbar-border: 2px solid var(--skill-neutral-300, #d1d5db);
  }

  .skill-toolbar--borderless {
    --toolbar-border: none;
    --toolbar-shadow: none;
  }

  .skill-toolbar--elevated {
    --toolbar-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    --toolbar-border: none;
  }

  /* 浮动模式 */
  .skill-toolbar--floating {
    --toolbar-border-radius: var(--skill-radius-lg, 8px);
    --toolbar-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
    --toolbar-border: none;
  }

  /* 透明模式 */
  .skill-toolbar--transparent {
    --toolbar-bg: transparent;
    --toolbar-border: none;
    --toolbar-shadow: none;
  }

  /* 粘性模式 */
  .skill-toolbar--sticky {
    position: sticky;
    top: 0;
    z-index: 100;
  }

  /* 响应式设计 */
  @media (max-width: 768px) {
    :host {
      --toolbar-height: 48px;
      --toolbar-item-size: 36px;
      --toolbar-padding: 0 var(--skill-spacing-sm, 8px);
    }

    .skill-toolbar__search {
      min-width: 150px;
    }

    .skill-toolbar__text {
      display: none;
    }

    .skill-toolbar__divider {
      margin: 0 var(--skill-spacing-xs, 4px);
    }
  }

  @media (max-width: 480px) {
    .skill-toolbar__search {
      display: none;
    }

    .skill-toolbar__item--text-only {
      padding: var(--skill-spacing-xs, 4px) var(--skill-spacing-sm, 8px);
      font-size: var(--skill-font-body-3, 12px);
    }
  }

  /* 深色模式支持 */
  @media (prefers-color-scheme: dark) {
    :host {
      --toolbar-bg: var(--skill-neutral-800, #1f2937);
      --toolbar-border: 1px solid var(--skill-neutral-600, #4b5563);
      --toolbar-text-color: var(--skill-text-primary-dark, #f9fafb);
      --toolbar-icon-color: var(--skill-neutral-400, #9ca3af);
      --toolbar-hover-bg: var(--skill-neutral-700, #374151);
      --toolbar-active-bg: var(--skill-primary-900, #1e3a8a);
    }

    .skill-toolbar--secondary {
      --toolbar-bg: var(--skill-neutral-700, #374151);
      --toolbar-border: 1px solid var(--skill-neutral-600, #4b5563);
      --toolbar-hover-bg: var(--skill-neutral-600, #4b5563);
    }

    .skill-toolbar__search-input {
      background-color: var(--skill-neutral-700, #374151);
      border-color: var(--skill-neutral-600, #4b5563);
      color: var(--skill-text-primary-dark, #f9fafb);
    }

    .skill-toolbar__search-input::placeholder {
      color: var(--skill-neutral-400, #9ca3af);
    }

    .skill-toolbar__dropdown-menu {
      background-color: var(--skill-neutral-800, #1f2937);
      border-color: var(--skill-neutral-600, #4b5563);
    }
  }

  /* 高对比度模式 */
  @media (prefers-contrast: high) {
    .skill-toolbar__item {
      border: 1px solid transparent;
    }

    .skill-toolbar__item:hover,
    .skill-toolbar__item--active {
      border-color: var(--skill-primary-500, #3b82f6);
    }

    .skill-toolbar__search-input {
      border-width: 2px;
    }

    .skill-toolbar__dropdown-menu {
      border-width: 2px;
    }
  }

  /* 减少动画模式 */
  @media (prefers-reduced-motion: reduce) {
    .skill-toolbar__item,
    .skill-toolbar__dropdown-menu,
    .skill-toolbar__search-input,
    .skill-toolbar__tab {
      transition: none;
    }
  }

  /* 打印样式 */
  @media print {
    .skill-toolbar {
      background: white !important;
      color: black !important;
      box-shadow: none !important;
      border: 1px solid black !important;
    }

    .skill-toolbar__dropdown-menu {
      display: none !important;
    }
  }

  /* 插槽样式 */
  ::slotted([slot="left"]) {
    display: flex;
    align-items: center;
    gap: var(--toolbar-gap);
  }

  ::slotted([slot="center"]) {
    display: flex;
    align-items: center;
    gap: var(--toolbar-gap);
  }

  ::slotted([slot="right"]) {
    display: flex;
    align-items: center;
    gap: var(--toolbar-gap);
  }

  /* 焦点管理 */
  .skill-toolbar:focus-within {
    outline: 2px solid var(--skill-primary-500, #3b82f6);
    outline-offset: 2px;
  }

  /* 加载状态 */
  .skill-toolbar__item--loading {
    position: relative;
    color: transparent !important;
  }

  .skill-toolbar__item--loading::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 16px;
    height: 16px;
    margin: -8px 0 0 -8px;
    border: 2px solid var(--toolbar-icon-color);
    border-top-color: transparent;
    border-radius: 50%;
    animation: spin 1s linear infinite;
  }

  @keyframes spin {
    to {
      transform: rotate(360deg);
    }
  }

  /* 拖拽状态 */
  .skill-toolbar__item--dragging {
    opacity: 0.5;
    transform: scale(0.95);
  }

  .skill-toolbar__item--drop-target {
    background-color: var(--toolbar-active-bg);
    border: 2px dashed var(--skill-primary-500, #3b82f6);
  }
`

css`
  :host {
    display: block;
    width: 100%;
    font-family: var(--skill-font-family, var(--chart-font-family, system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif));
    line-height: var(--skill-line-height, 1.5);
    color: var(--chart-text-color, #111827);
  }

  .skill-bar-chart {
    position: relative;
    width: 100%;
    height: 100%;
    overflow: hidden;
    border-radius: var(--chart-border-radius, var(--skill-border-radius-md, 0.375rem));
    background: var(--chart-background, var(--skill-color-background, #FFFFFF));
  }

  .skill-bar-chart__container {
    position: relative;
    width: 100%;
    height: 100%;
  }

  .skill-bar-chart__svg {
    width: 100%;
    height: 100%;
    display: block;
  }

  .skill-bar-chart__title {
    text-align: center;
    margin-bottom: var(--chart-spacing-md, 1rem);
    font-size: var(--chart-font-size-lg, 1.125rem);
    font-weight: var(--chart-font-weight-bold, 700);
    color: var(--chart-text-color, #111827);
  }

  .skill-bar-chart__subtitle {
    text-align: center;
    margin-bottom: var(--chart-spacing-lg, 1.5rem);
    font-size: var(--chart-font-size-sm, 0.875rem);
    color: var(--chart-text-secondary-color, #6B7280);
  }

  .skill-bar-chart__loading {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    background: var(--chart-loading-background, rgba(255, 255, 255, 0.9));
    backdrop-filter: blur(2px);
    z-index: var(--chart-z-loading, 10);
  }

  .skill-bar-chart__empty {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    color: var(--chart-empty-color, #6B7280);
    font-size: var(--chart-font-size-md, 1rem);
    z-index: var(--chart-z-empty, 5);
  }

  .skill-bar-chart__empty-icon {
    margin-bottom: var(--chart-spacing-md, 1rem);
    opacity: 0.5;
  }

  .skill-bar-chart__empty-text {
    font-size: var(--chart-font-size-sm, 0.875rem);
  }

  /* SVG Elements */
  .skill-bar-chart__grid {
    stroke: var(--chart-grid-color, #E5E7EB);
    stroke-width: 1;
    stroke-dasharray: 2, 2;
    opacity: 0.7;
  }

  .skill-bar-chart__grid--horizontal {
    stroke-dasharray: 2, 2;
  }

  .skill-bar-chart__axis {
    stroke: var(--chart-axis-color, #9CA3AF);
    stroke-width: 1;
    fill: none;
  }

  .skill-bar-chart__axis-label {
    fill: var(--chart-text-secondary-color, #6B7280);
    font-size: var(--chart-font-size-xs, 0.75rem);
    font-family: inherit;
  }

  .skill-bar-chart__axis-title {
    fill: var(--chart-text-color, #111827);
    font-size: var(--chart-font-size-sm, 0.875rem);
    font-weight: var(--chart-font-weight-medium, 500);
    font-family: inherit;
  }

  .skill-bar-chart__bar {
    transition: fill-opacity 0.2s ease, stroke-width 0.2s ease, cursor 0.2s ease;
    cursor: pointer;
  }

  .skill-bar-chart__bar:hover {
    fill-opacity: 0.8;
    stroke-width: 2;
  }

  .skill-bar-chart__bar--stacked {
    stroke: white;
    stroke-width: 1;
  }

  .skill-bar-chart__bar--disabled {
    opacity: 0.4;
    cursor: not-allowed;
  }

  .skill-bar-chart__bar--disabled:hover {
    fill-opacity: 0.4;
    stroke-width: 1;
  }

  /* Bar values */
  .skill-bar-chart__value {
    fill: var(--chart-value-color, #374151);
    font-size: var(--chart-font-size-xs, 0.75rem);
    font-weight: var(--chart-font-weight-medium, 500);
    text-anchor: middle;
    pointer-events: none;
    opacity: 0;
    transition: opacity 0.2s ease;
  }

  .skill-bar-chart__value--visible {
    opacity: 1;
  }

  .skill-bar-chart__value--outside {
    fill: var(--chart-text-color, #111827);
  }

  /* Interactions */
  .skill-bar-chart__interaction-area {
    fill: transparent;
    cursor: pointer;
  }

  .skill-bar-chart__interaction-area:hover {
    fill: rgba(0, 0, 0, 0.05);
  }

  /* Tooltip */
  .skill-bar-chart__tooltip {
    position: absolute;
    padding: var(--chart-tooltip-padding, 0.5rem);
    background: var(--chart-tooltip-background, #1F2937);
    color: var(--chart-tooltip-color, #FFFFFF);
    border-radius: var(--chart-tooltip-border-radius, 0.375rem);
    font-size: var(--chart-font-size-xs, 0.75rem);
    pointer-events: none;
    z-index: var(--chart-z-tooltip, 100);
    opacity: 0;
    transition: opacity 0.2s ease;
    box-shadow: var(--chart-tooltip-shadow, 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06));
  }

  .skill-bar-chart__tooltip--visible {
    opacity: 1;
  }

  /* Legend */
  .skill-bar-chart__legend {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    margin-top: var(--chart-spacing-md, 1rem);
    gap: var(--chart-spacing-md, 1rem);
  }

  .skill-bar-chart__legend-item {
    display: flex;
    align-items: center;
    gap: var(--chart-spacing-sm, 0.5rem);
    cursor: pointer;
    padding: var(--chart-spacing-xs, 0.25rem);
    border-radius: var(--chart-border-radius-sm, 0.125rem);
    transition: background-color 0.2s ease;
  }

  .skill-bar-chart__legend-item:hover {
    background: var(--chart-legend-hover-background, rgba(0, 0, 0, 0.05));
  }

  .skill-bar-chart__legend-item--disabled {
    opacity: 0.4;
  }

  .skill-bar-chart__legend-color {
    width: 12px;
    height: 12px;
    border-radius: 2px;
    border: 1px solid transparent;
  }

  .skill-bar-chart__legend-label {
    font-size: var(--chart-font-size-sm, 0.875rem);
    color: var(--chart-text-color, #111827);
  }

  /* Responsive Design */
  @media (max-width: 768px) {
    .skill-bar-chart__title {
      font-size: var(--chart-font-size-md, 1rem);
    }

    .skill-bar-chart__subtitle {
      font-size: var(--chart-font-size-xs, 0.75rem);
      margin-bottom: var(--chart-spacing-md, 1rem);
    }

    .skill-bar-chart__legend {
      gap: var(--chart-spacing-sm, 0.5rem);
    }

    .skill-bar-chart__legend-label {
      font-size: var(--chart-font-size-xs, 0.75rem);
    }

    .skill-bar-chart__axis-label {
      font-size: 0.625rem;
    }

    .skill-bar-chart__axis-title {
      font-size: 0.75rem;
    }

    .skill-bar-chart__value {
      font-size: 0.625rem;
    }
  }

  /* Dark Theme */
  :host([theme="dark"]) .skill-bar-chart {
    background: var(--chart-background, #1F2937);
    color: var(--chart-text-color, #F9FAFB);
  }

  :host([theme="dark"]) .skill-bar-chart__grid {
    stroke: var(--chart-grid-color, #374151);
  }

  :host([theme="dark"]) .skill-bar-chart__axis {
    stroke: var(--chart-axis-color, #6B7280);
  }

  :host([theme="dark"]) .skill-bar-chart__axis-label {
    fill: var(--chart-text-secondary-color, #9CA3AF);
  }

  :host([theme="dark"]) .skill-bar-chart__axis-title {
    fill: var(--chart-text-color, #F9FAFB);
  }

  :host([theme="dark"]) .skill-bar-chart__bar--stacked {
    stroke: var(--chart-stack-stroke, #374151);
  }

  :host([theme="dark"]) .skill-bar-chart__value {
    fill: var(--chart-value-color, #D1D5DB);
  }

  :host([theme="dark"]) .skill-bar-chart__value--outside {
    fill: var(--chart-text-color, #F9FAFB);
  }

  :host([theme="dark"]) .skill-bar-chart__legend-item:hover {
    background: var(--chart-legend-hover-background, rgba(255, 255, 255, 0.05));
  }

  :host([theme="dark"]) .skill-bar-chart__legend-label {
    color: var(--chart-text-color, #F9FAFB);
  }

  /* Accessibility */
  .skill-bar-chart__visually-hidden {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
  }

  /* Focus styles */
  .skill-bar-chart:focus-visible {
    outline: 2px solid var(--chart-focus-color, #3B82F6);
    outline-offset: 2px;
  }

  .skill-bar-chart__bar:focus {
    outline: 2px solid var(--chart-focus-color, #3B82F6);
    outline-offset: 1px;
  }

  /* Loading state animations */
  @keyframes skill-bar-chart__pulse {
    0%, 100% {
      opacity: 1;
    }
    50% {
      opacity: 0.5;
    }
  }

  .skill-bar-chart__skeleton {
    animation: skill-bar-chart__pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
    background: linear-gradient(
      90deg,
      var(--chart-skeleton-base, #E5E7EB) 25%,
      var(--chart-skeleton-highlight, #F3F4F6) 50%,
      var(--chart-skeleton-base, #E5E7EB) 75%
    );
    background-size: 200% 100%;
    animation: skill-bar-chart__shimmer 1.5s ease-in-out infinite;
  }

  @keyframes skill-bar-chart__shimmer {
    0% {
      background-position: -200% 0;
    }
    100% {
      background-position: 200% 0;
    }
  }

  /* Print styles */
  @media print {
    .skill-bar-chart__tooltip,
    .skill-bar-chart__loading {
      display: none;
    }

    .skill-bar-chart__grid {
      opacity: 0.3;
    }

    .skill-bar-chart__bar:hover {
      fill-opacity: 1;
      stroke-width: 1;
    }
  }

  /* Orientation-specific styles */
  .skill-bar-chart--vertical .skill-bar-chart__value {
    text-anchor: middle;
  }

  .skill-bar-chart--horizontal .skill-bar-chart__value {
    text-anchor: start;
    dominant-baseline: middle;
  }

  /* Animation for bars */
  @keyframes skill-bar-chart__bar-grow {
    from {
      transform: scaleY(0);
    }
    to {
      transform: scaleY(1);
    }
  }

  .skill-bar-chart__bar--animated {
    animation: skill-bar-chart__bar-grow 0.6s ease-out;
    transform-origin: bottom;
  }

  .skill-bar-chart--horizontal .skill-bar-chart__bar--animated {
    transform-origin: left;
    animation: skill-bar-chart__bar-grow-horizontal 0.6s ease-out;
  }

  @keyframes skill-bar-chart__bar-grow-horizontal {
    from {
      transform: scaleX(0);
    }
    to {
      transform: scaleX(1);
    }
  }
`

